import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Checkout.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Checkout({ user }) {
  const navigate = useNavigate();
  const userId = user?.id;

  const [cartItems, setCartItems] = useState([]);
  const [loadingCart, setLoadingCart] = useState(true);
  const [checkoutLoading, setCheckoutLoading] = useState(false);

  // Redirect if not logged in
  useEffect(() => {
    if (!userId) {
      alert('Vui lòng đăng nhập để thanh toán');
      navigate('/login');
    }
  }, [userId, navigate]);

  // Fetch giỏ hàng từ API
  useEffect(() => {
    if (!userId) return;
    (async () => {
      try {
        const res = await axios.get(`/api/cart/${userId}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('Lấy giỏ hàng lỗi:', err);
        alert('Không thể tải giỏ hàng. Vui lòng thử lại sau.');
      } finally {
        setLoadingCart(false);
      }
    })();
  }, [userId]);

  // Tính tổng tiền
  const totalPrice = cartItems.reduce((sum, item) => {
    const raw = typeof item.Price === 'number'
      ? item.Price
      : parseFloat(item.Price) || 0;
    const final = item.Discount > 0
      ? raw * (1 - item.Discount / 100)
      : raw;
    return sum + final;
  }, 0);

  // Xử lý thanh toán
  const handleCheckout = async () => {
    if (!cartItems.length) {
      alert('Giỏ hàng đang trống.');
      return;
    }
    setCheckoutLoading(true);

    // Lấy danh sách cartItemIds
    const cartItemIds = cartItems.map(item => item.cartItemId);

    // 1) Thực hiện thanh toán
    let checkoutSuccess = false;
    try {
      const res = await axios.post('/api/checkout', { userId, cartItemIds });
      // Chỉ tính là success khi backend trả status 2xx và kèm purchaseId
      if (res.status >= 200 && res.status < 300 && res.data.purchaseId) {
        checkoutSuccess = true;
      } else {
        throw new Error(res.data.message || 'Thanh toán lỗi');
      }
    } catch (err) {
      console.error('Checkout lỗi:', err);
      alert(err.response?.data.message || 'Thanh toán thất bại.');
      setCheckoutLoading(false);
      return;  // Dừng ở đây, không thêm vào thư viện
    }

    // 2) Nếu thanh toán thành công, mới thêm game vào thư viện
    try {
      // Batch add
      await axios.post(`/api/users/${userId}/library`, { gameIds: cartItemIds });
    } catch (batchErr) {
      console.warn('Batch add to library failed, fallback to individual adds:', batchErr);
      for (const id of cartItemIds) {
        try {
          await axios.post(`/api/users/${userId}/library`, { gameId: id });
        } catch (singleErr) {
          console.error(`Add game ${id} to library failed:`, singleErr);
        }
      }
    }

    alert('Thanh toán thành công!');
    setCheckoutLoading(false);
    navigate('/library');
  };


  if (loadingCart) {
    return <div className="checkout-page"><p>Đang tải giỏ hàng...</p></div>;
  }

  return (
    <div className="checkout-page">
      <header className="store-header">
        <div className="nav">
          <div className="steam-logo" onClick={() => navigate('/')}>My Game Store</div>
          <div className="user-actions">
            <button className="download-btn" onClick={() => navigate(-1)}>← Quay lại</button>
            <button className="cart-btn" onClick={() => navigate('/cart')}>Giỏ hàng</button>
          </div>
        </div>
      </header>

      <main className="checkout-main">
        <div className="checkout-container">
          <h2>Thanh toán</h2>
          {cartItems.length === 0 ? (
            <p>Giỏ hàng của bạn đang trống.</p>
          ) : (
            <>
              <div className="order-summary">
                <h3>Tóm tắt đơn hàng</h3>
              </div>
              <ul className="checkout-cart-list">
                {cartItems.map(item => {
                  const raw = typeof item.Price === 'number'
                    ? item.Price
                    : parseFloat(item.Price) || 0;
                  const final = item.Discount > 0
                    ? raw * (1 - item.Discount / 100)
                    : raw;
                  return (
                    <li className="checkout-cart-item" key={item.cartItemId}>
                      <span className="checkout-cart-item-name">{item.Name}</span>
                      <span className="checkout-cart-item-price">{final.toFixed(2)}</span>
                    </li>
                  );
                })}
              </ul>

              <div className="checkout-footer">
                <div className="checkout-cart-total">Tổng tiền: {totalPrice.toFixed(2)}</div>
                <button
                  className="checkout-btn"
                  onClick={handleCheckout}
                  disabled={checkoutLoading}
                >
                  {checkoutLoading ? 'Đang xử lý...' : 'Thanh toán'}
                </button>
              </div>
            </>
          )}
        </div>
      </main>

      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Logo" className="footer-logo" />
            <p>© 2025 My Game Store</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy</li>
              <li>Terms</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
