import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/AdminPanel.css';

const AdminPanel = ({ onLogout, user }) => {
  const token = sessionStorage.getItem('token');
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [search, setSearch] = useState('');
  const [userPage, setUserPage] = useState(1);
  const [orderPage, setOrderPage] = useState(1);
  const [totalUsers, setTotalUsers] = useState(0);
  const [totalOrders, setTotalOrders] = useState(0);
  const pageSize = 5;

  useEffect(() => {
    fetchUsers();
    // eslint-disable-next-line
  }, [search, userPage]);

  useEffect(() => {
    fetchOrders();
    // eslint-disable-next-line
  }, [orderPage]);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('/api/admin/users', {
        params: { search, page: userPage, pageSize },
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data.users);
      setTotalUsers(res.data.total); // backend cần trả về total
    } catch (err) {
      console.error('Error fetching users', err);
    }
  };

  const handleUpdateUser = async (user) => {
    const newName = prompt('Nhập tên mới:', user.Name);
    const newRole = prompt('Nhập vai trò (user/admin):', user.role);
    const newAccount = prompt('Nhập số tài khoản ngân hàng:', user.accountNumberBank || '');
    if (newName != null && newRole != null) {
      try {
        await axios.put(
          `/api/admin/users/${user.id}`,
          { name: newName, role: newRole, accountNumberBank: newAccount },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        fetchUsers();
      } catch (err) {
        console.error('Error updating user', err);
      }
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Bạn có chắc muốn xóa người dùng này?')) return;
    try {
      await axios.delete(`/api/admin/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUsers();
    } catch (err) {
      console.error('Error deleting user', err);
    }
  };

  const fetchOrders = async () => {
    try {
      const res = await axios.get('/api/admin/orders', {
        params: { page: orderPage, pageSize },
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrders(res.data.orders);
      setTotalOrders(res.data.total); // backend cần trả về total
    } catch (err) {
      console.error('Error fetching orders', err);
    }
  };

  const handleViewOrder = async (orderId) => {
    try {
      const res = await axios.get(`/api/admin/orders/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(JSON.stringify(res.data.orderDetails, null, 2));
    } catch (err) {
      console.error('Error fetching order details', err);
    }
  };

  const totalUserPages = Math.ceil(totalUsers / pageSize);
  const totalOrderPages = Math.ceil(totalOrders / pageSize);

  return (
    <div className="admin-panel-container">
      <div className="admin-panel-header">
        <h1 className="admin-panel-title">Admin Panel</h1>
        <button className="admin-logout-btn" onClick={onLogout}>
          <svg width="21" height="21" fill="none" stroke="currentColor" strokeWidth="2"
            strokeLinecap="round" strokeLinejoin="round" viewBox="0 0 24 24">
            <path d="M17 16l4-4m0 0l-4-4m4 4H7"></path>
            <path d="M9 20H5a2 2 0 0 1-2-2V6a2 2 0 0 1 2-2h4"></path>
          </svg>
          Đăng xuất
        </button>
      </div>

      <section className="admin-section">
        <h2>Quản lý Người dùng</h2>
        <input
          type="text"
          placeholder="Tìm kiếm người dùng"
          value={search}
          onChange={e => { setSearch(e.target.value); setUserPage(1); }}
        />
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID</th><th>Name</th><th>Email</th><th>Role</th><th>Account</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.Name}</td>
                <td>{user.Email}</td>
                <td>{user.role}</td>
                <td>{user.accountNumberBank}</td>
                <td className="admin-actions">
                  <button className="admin-btn edit" onClick={() => handleUpdateUser(user)}>Update</button>
                  <button className="admin-btn delete" onClick={() => handleDeleteUser(user.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="admin-pagination">
          <button className="admin-btn" disabled={userPage <= 1} onClick={() => setUserPage(userPage - 1)}>Prev</button>
          <span>Trang {userPage} / {totalUserPages || 1}</span>
          <button className="admin-btn" disabled={userPage >= totalUserPages} onClick={() => setUserPage(userPage + 1)}>Next</button>
        </div>
      </section>

      <section className="admin-section">
        <h2>Quản lý Đơn hàng</h2>
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID</th><th>Customer</th><th>Ngày</th><th>Tổng tiền</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order.id}>
                <td>{order.id}</td>
                <td>{order.customerName} ({order.Email})</td>
                <td>{new Date(order.purchase_date).toLocaleString()}</td>
                <td>{order.total_amount}</td>
                <td className="admin-actions">
                  <button className="admin-btn" onClick={() => handleViewOrder(order.id)}>View</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="admin-pagination">
          <button className="admin-btn" disabled={orderPage <= 1} onClick={() => setOrderPage(orderPage - 1)}>Prev</button>
          <span>Trang {orderPage} / {totalOrderPages || 1}</span>
          <button className="admin-btn" disabled={orderPage >= totalOrderPages} onClick={() => setOrderPage(orderPage + 1)}>Next</button>
        </div>
      </section>
    </div>
  );
};

export default AdminPanel;
