import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/Profile.css";

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Profile({ user, onLogout }) {
  const navigate = useNavigate();
  const [ordersHistory, setOrdersHistory] = useState([]);    // ← mới
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    axios.defaults.headers.common["Authorization"] = `Bearer ${sessionStorage.getItem("token")}`;
    const userId = user.id || user.userId || user._id;

    // ----- Lịch sử mua hàng -----
    axios.get(`/api/orders`, { params: { userId } })
      .then(res => {
        // Giả sử API trả về { orders: [...] }
        setOrdersHistory(res.data.orders || []);
      })
      .catch(err => console.error("Error fetching order history:", err));

    // Fetch user reviews (giữ nguyên)
    axios.get(`/api/users/${userId}/reviews`)
      .then(res => {
        const payload = res.data;
        let list = Array.isArray(payload)
          ? payload
          : Array.isArray(payload.data)
            ? payload.data
            : Array.isArray(payload.reviews)
              ? payload.reviews
              : [];
        setReviews(list);
      })
      .catch(err => console.error("Error fetching reviews:", err));
  }, [user, navigate]);

  if (!user) {
    return <div className="loading">Loading profile…</div>;
  }

  return (
    <div className="profile-page">
      {/* HEADER */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

      {/* THÔNG TIN CÁ NHÂN */}
      <section className="user-info">
        <h3>Thông tin cá nhân</h3>
        <p><strong>Tên:</strong> {user.name || user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
        {user.createdAt && (
          <p>
            <strong>Ngày tạo:</strong>{" "}
            {new Date(user.createdAt).toLocaleDateString()}
          </p>
        )}
      </section>

      {/* MAIN */}
      <main className="profile-container">

        {/* LỊCH SỬ MUA HÀNG */}
        <section className="purchase-history">
          <h3>Lịch sử mua hàng</h3>
          {ordersHistory.length > 0 ? (
            <ul className="orders-list">
              {ordersHistory.map(o => (
                <li key={o.id || o.orderId}>
                  <span className="order-id">#{o.id}</span>
                  <span className="order-date">
                    {new Date(o.purchase_date).toLocaleDateString()}
                  </span>
                  <span className="order-total">
                    {o.total_amount?.toLocaleString()} đ
                  </span>
                  <button onClick={() => navigate(`/orders/${o.id}`)}>
                    Xem chi tiết
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p className="placeholder-text">Bạn chưa có đơn hàng nào.</p>
          )}
        </section>

        {/* Your Reviews (giữ nguyên) */}
        <section className="user-reviews">
          <h3>Your Reviews</h3>
          {reviews.length > 0 ? (
            <ul className="reviews-list">
              {reviews.map(r => (
                <li key={r.id || r.reviewId} className="review-item">
                  <strong>{r.title || 'Untitled Review'}</strong>
                  <p>{r.content || r.comment}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p className="placeholder-text">You haven’t written any reviews yet.</p>
          )}
        </section>

        <button className="logout-btn" onClick={onLogout}>
          Đăng xuất
        </button>
      </main>

      {/* FOOTER */}
      <footer className="footer">
        {/* ... giữ nguyên footer ... */}
      </footer>
    </div>
  );
}
