import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

/**
 * Chuyển val (string hoặc number) thành Number hợp lệ
 * - Hỗ trợ các định dạng: "1.234.567", "1,234,567.89", "1.234.567,89"
 * - Trả về null nếu không parse được
 */
function safePrice(val) {
  if (val == null || val === '') return null;

  let str = String(val).trim();

  // Kiểu "1.234.567,89" (dot thousands, comma decimal)
  if (/^\d{1,3}(\.\d{3})*,\d+$/.test(str)) {
    str = str.replace(/\./g, '').replace(',', '.');
  } else {
    // Nếu có nhiều dấu chấm (ví dụ "1.234.567.89"), coi các chấm giữa là tách nghìn
    const parts = str.split('.');
    if (parts.length > 2) {
      const decimal = parts.pop();
      const integer = parts.join('');
      str = integer + '.' + decimal;
    }
    // Loại bỏ dấu phẩy nghìn
    str = str.replace(/,/g, '');
  }

  const num = Number(str);
  return Number.isFinite(num) ? num : null;
}

// Thiết lập baseURL chung cho axios
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [allGames, setAllGames] = useState([]);
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const navigate = useNavigate();

  // Fetch toàn bộ game 1 lần, sau đó lọc client-side
  useEffect(() => {
    const fetchAllGames = async () => {
      try {
        const { data: games } = await axios.get('/api/games');
        setAllGames(games);
        setFeatured(games.filter(g => g.featured));
        setSpecial(games.filter(g => g.special));
        setFreeGames(games.filter(g => safePrice(g.price) === 0));
        const sorted = [...games].sort((a, b) => b.purchases - a.purchases);
        setBestSellers(sorted.slice(0, 10));
      } catch (error) {
        console.error('Error fetching all games:', error);
      }
    };
    fetchAllGames();
  }, []);

  // Các handler ví dụ: addToCart, pagination, ...
  // function addToCart(game) { ... }

  return (
    <div className="store-container">
      {/* SPECIAL DEALS */}
      <section className="special-deals">
        <h2>SPECIAL DEALS</h2>
        <div className="deal-list">
          {special.map(game => {
            const priceNum = safePrice(game.price);
            return (
              <div key={game.id} className="deal-card">
                <img src={game.image} alt={game.title} />
                <h3>{game.title}</h3>
                <span>
                  {priceNum != null
                    ? priceNum.toLocaleString('en-US', {
                        style: 'currency', currency: 'USD', minimumFractionDigits: 2
                      })
                    : 'Free'}
                </span>
                <p>{game.purchases.toLocaleString('vi-VN')} lượt mua</p>
              </div>
            );
          })}
        </div>
        {/* Pagination controls có thể thêm tại đây */}
      </section>

      {/* FREE GAMES */}
      <section className="free-games">
        <h2>FREE GAMES</h2>
        <div className="game-list">
          {freeGames.map(game => (
            <div key={game.id} className="game-card">
              <img src={game.image} alt={game.title} />
              <h3>{game.title}</h3>
              <span>Free</span>
              <p>{game.purchases.toLocaleString('vi-VN')} lượt mua</p>
            </div>
          ))}
        </div>
      </section>

      {/* BESTSELLERS */}
      <section className="bestsellers">
        <h2>BESTSELLERS</h2>
        <div className="game-list">
          {bestSellers.map(game => {
            const priceNum = safePrice(game.price);
            return (
              <div key={game.id} className="game-card">
                <img src={game.image} alt={game.title} />
                <h3>{game.title}</h3>
                <span>
                  {priceNum != null
                    ? priceNum.toLocaleString('en-US', {
                        style: 'currency', currency: 'USD', minimumFractionDigits: 2
                      })
                    : 'Free'}
                </span>
                <p>{game.purchases.toLocaleString('vi-VN')} lượt mua</p>
              </div>
            );
          })}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img
              src="/logo.png"
              alt="Valve Steam Logo"
              className="footer-logo"
            />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US
              and other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
