import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Style/Library.css";

const Library = () => {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  // Lấy userId từ session hoặc param URL
  const storedUser = sessionStorage.getItem("user");
  const parsedUser = storedUser ? JSON.parse(storedUser) : null;
  const currentUserId = parsedUser?.id;
  const userId = paramUserId || currentUserId;
  const [page, setPage] = useState(1);        // Trang hiện tại
  const [pageSize, setPageSize] = useState(10); // Số game trên 1 trang (tuỳ chỉnh)
  const [total, setTotal] = useState(0);      // Tổng số game

  // State games + loading + search query
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState("");

  // Fetch library data
  useEffect(() => {
    const fetchLibrary = async () => {
      setLoading(true);
      try {
        if (!userId) throw new Error("User ID is missing");
        let res;
        try {
          res = await axios.get(`/api/users/${userId}/library?page=${page}&pageSize=${pageSize}`);
        } catch (err) {
          if (err.response?.status === 404) {
            try {
              res = await axios.get(`/api/users/${userId}/games?page=${page}&pageSize=${pageSize}`);
            } catch {
              res = await axios.get(`/api/library/${userId}?page=${page}&pageSize=${pageSize}`);
            }
          } else {
            throw err;
          }
        }

        const data = res.data;
        let rawItems = [];
        if (Array.isArray(data)) rawItems = data;
        else rawItems = data.items || data.data || data.library || data.games || [];
        setTotal(data.total || rawItems.length); // lấy total từ API (nếu có)

        // Nếu rawItems là IDs, fetch chi tiết
        const ids = rawItems
          .map(item => item.id ?? item.gameId ?? item)
          .filter(id => id);

        if (ids.length && !rawItems[0].title) {
          const detailRes = await Promise.all(ids.map(id => axios.get(`/api/games/${id}`)));
          setGames(detailRes.map(r => r.data));
        } else {
          setGames(rawItems);
        }
      } catch (error) {
        setGames([]);
      } finally {
        setLoading(false);
      }
    };
    fetchLibrary();
  }, [userId, page, pageSize]);


  // Filter theo query
  const filteredGames = games.filter(game =>
    game.title?.toLowerCase().includes(query.toLowerCase())
  );
  // 2. Phân trang dựa trên filteredGames
const startIdx = (page - 1) * pageSize;
const endIdx = startIdx + pageSize;
const pagedGames = filteredGames.slice(startIdx, endIdx);

// Tổng số trang cho filteredGames
const totalFiltered = filteredGames.length;
const totalPages = Math.ceil(totalFiltered / pageSize);

  if (loading) {
    return <div className="loading">Loading games for user {userId}...</div>;
  }

  return (
    <>
      {/* HEADER */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart </button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

      <div className="library-container">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search games..."
            value={query}
            onChange={e => setQuery(e.target.value)}
          />
        </div>

        {filteredGames.length === 0 ? (
          <div className="no-games">Không tìm thấy game nào.</div>
        ) : (
          <div className="library-games">
            {filteredGames.map(game => (
              <div key={game.id} className="library-game-card">
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <button
                  className="play-button"
                  onClick={() => { /* play logic */ }}
                >
                  Play
                </button>
              </div>
            ))}
          </div>
        )}
        <div className="pagination" style={{ textAlign: "center", margin: "20px 0" }}>
  <button
    onClick={() => setPage(page - 1)}
    disabled={page === 1}
    className="btn-page"
  >
    Previous
  </button>
  <span style={{ margin: "0 12px" }}>
    Page {page} / {Math.ceil(total / pageSize)}
  </span>
  <button
    onClick={() => setPage(page + 1)}
    disabled={page >= Math.ceil(total / pageSize)}
    className="btn-page"
  >
    Next
  </button>
</div>
      </div>
    </>
  );
};

export default Library;
