import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function GameDetail() {
  const { gameId } = useParams();
  const navigate = useNavigate();

  // Thay bằng userId thật từ auth
  const userId = 1;

  const [game, setGame] = useState(null);
  const [inCart, setInCart] = useState(false);

  // Khi component mount, fetch chi tiết game và kiểm tra giỏ
  useEffect(() => {
    // Lấy chi tiết game
    axios.get(`/api/games/${gameId}`)
      .then(res => setGame(res.data))
      .catch(err => console.error('Lấy game lỗi:', err));

    // Kiểm tra xem đã thêm vào giỏ chưa
    axios.get(`/api/cart/${userId}`)
      .then(res => {
        const exists = res.data.items.some(
          item => item.gameId === parseInt(gameId, 10)
        );
        setInCart(exists);
      })
      .catch(err => console.error('Kiểm tra giỏ lỗi:', err));
  }, [gameId, userId]);

  if (!game) return <div>Loading...</div>;

  const handleAddToCart = async () => {
    try {
      const res = await axios.post('/api/cart/add', { userId, gameId: parseInt(gameId, 10) });
      if (res.status === 201) {
        setInCart(true);
        alert('Đã thêm vào giỏ hàng!');
        navigate('/cart');
      } else {
        alert(res.data.message || 'Game đã có trong giỏ hàng');
      }
    } catch (err) {
      console.error('Thêm giỏ lỗi:', err);
      alert('Lỗi khi thêm vào giỏ hàng.');
    }
  };

  return (
    <div className="game-detail">
      <header className="detail-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/')}>GameStore</span>
          <nav>
            <a onClick={() => navigate('/store')}>Store</a>
            <a onClick={() => navigate(-1)}>Back</a>
          </nav>
        </div>
      </header>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}

      {game && (
        <>
          <div className="detail-banner">
            <img src={game.screenshotUrl} alt={game.title} />
          </div>

          <div className="detail-main">
            <div className="detail-left">
              <h2>{game.title}</h2>
              <p className="description">{game.description}</p>

              <div className="detail-buy">
                <div className="price">${game.price.toFixed(2)}</div>
                <button 
        className="buy-button" 
        onClick={handleAddToCart}
      >
        Add to Cart
      </button>
              </div>

              <hr />

              <div className="reviews-list">
                {reviews.map(r => (
                  <div key={r.id} className="review-item">
                    <strong>{r.user.name}</strong>
                    <p>{r.comment}</p>
                    <small>Rating: {r.rating}/5</small>
                  </div>
                ))}
                {reviews.length === 0 && <p>No reviews yet.</p>}
              </div>
            </div>

            <div className="detail-right">
              <img className="detail-screenshot" src={game.screenshotUrl} alt="Screenshot" />
              <div className="system-req">
                <h3>System Requirements</h3>
                <ul>
                  <li>OS: Windows 10</li>
                  <li>CPU: Intel i5</li>
                  <li>RAM: 8 GB</li>
                  <li>GPU: GTX 970</li>
                </ul>
              </div>
            </div>
          </div>

          <section className="detail-extras">
            <h3>Additional Information</h3>
            <hr />
          </section>

          <section className="submit-review">
            <h4>Write a Review</h4>
            <form onSubmit={handleReviewSubmit}>
              <div className="form-group">
                <label>Rating</label>
                <select className="rating-select" value={rating} onChange={e => setRating(e.target.value)} required>
                  <option value="">Select</option>
                  {[1,2,3,4,5].map(n => <option key={n} value={n}>{n}</option>)}
                </select>
              </div>
              <div className="form-group">
                <label>Comment</label>
                <textarea className="review-textarea" value={comment} onChange={e => setComment(e.target.value)} required />
              </div>
              <button type="submit" className="submit-btn">Submit Review</button>
            </form>
          </section>

          <footer className="detail-footer">
            <div className="footer-content">
              <ul>
                <li>Privacy Policy</li>
                <li>Terms of Service</li>
              </ul>
            </div>
          </footer>
        </>
      )}

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Thank you for your review!</p>
            <button onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
