import axios from 'axios';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api'
});

const apiService = {
  // Lấy danh sách game (có phân trang & lọc type)
  getGames: ({ page = 1, pageSize = 4, type, search }) =>
    API.get('/games', { params: { page, pageSize, type, search } })
       .then(res => res.data),

  // Đăng ký / đăng nhập
  registerUser: ({ name, email, password, accountNumberBank }) =>
    API.post('/auth/register', { name, email, password, accountNumberBank })
       .then(res => res.data),

  loginUser: ({ email, password }) =>
    API.post('/auth/login', { email, password })
       .then(res => res.data),

  // Các API khác tùy nhu cầu (ví dụ reviews, cart...)
  // getGameReviews: (gameId) => API.get(`/games/${gameId}/reviews`).then(r=>r.data),
  // addGameReview: (gameId, review) => API.post(`/games/${gameId}/reviews`, review).then(r=>r.data),
  // addToCart: (gameId) => API.post('/cart', { gameId }).then(r=>r.data),
  // getCart: () => API.get('/cart').then(r=>r.data),
};

export default apiService;

/**
 * Lấy games với filter, pagination
 * params: { type, page, pageSize, search }
 */
export function getStoreGames(params = {}) {
  return API.get('/api/games', { params }).then(res => res.data);
}

export function loginUser(credentials) {
  return API.post('/api/auth/login', credentials).then(res => res.data);
}

export function registerUser(data) {
  return API.post('/api/auth/register', data).then(res => res.data);
}

export function getGameReviews({ gameId }) {
  return API.get('/api/reviews', { params: { gameId } }).then(res => res.data);
}

export function addToCart({ userId, gameId }) {
  return API.post('/api/cart', { userId, gameId }).then(res => res.data);
}

export function getCart(userId) {
  return API.get('/api/cart', { params: { userId } }).then(res => res.data);
}
