// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Thiết lập Base URL cho Axios
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  const navigate = useNavigate();
  const { slug } = useParams();
  const userId = 1; // sử dụng mặc định userId=1 nếu chưa có auth

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');

  // Fetch game detail
  useEffect(() => {
    const fetchGame = async () => {
      try {
        const res = await axios.get(`/api/games/${slug}`);
        setGame(res.data);
      } catch (err) {
        setError(err.response?.status === 404 ? 'Game not found' : 'Error fetching data');
      } finally {
        setLoading(false);
      }
    };
    fetchGame();
  }, [slug]);

  // Fetch reviews for this game
  useEffect(() => {
    if (!game) return;
    const fetchReviews = async () => {
      try {
        const res = await axios.get(`/api/reviews`, { params: { gameId: game.id } });
        setReviews(res.data);
      } catch (err) {
        console.error('Error fetching reviews:', err);
      }
    };
    fetchReviews();
  }, [game]);

  const handleBuy = () => {
    // logic mua
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/reviews', {
        userId: userId,
        gameId: game.id,
        comment: review,
        rating: Number(rating)
      });
      setShowModal(true);
      // Refetch reviews
      const res = await axios.get(`/api/reviews`, { params: { gameId: game.id } });
      setReviews(res.data);
      setRating('');
      setReview('');
    } catch (err) {
      console.error(err);
      alert('Failed to submit review');
    }
  };

  if (loading) return <div className="game-detail"><p>Loading...</p></div>;
  if (error)   return <div className="detail-not-found"><p>{error}</p></div>;

  // Determine fields
  const priceValue   = ('price' in game)     ? game.price     : game.Price;
  const publisherVal = ('publisher' in game) ? game.publisher : game.Publisher;
  const developerVal = ('developer' in game) ? game.developer : game.Developer;

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="detail-header">
        <div className="nav">
          <div className="steam-logo" onClick={() => navigate('/')}>STEAM</div>
          <nav>
            <a onClick={() => navigate('/')}>Home</a>
            <a href="#extras">Extras</a>
          </nav>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={game.imageUrl || placeholderBanner} alt={game.title} />
      </div>

      {/* Main */}
      <main className="detail-main">
        <div className="detail-left">
          <h2>{game.title}</h2>
          <p className="description">{game.description}</p>

          <div className="detail-buy">
            <span className="price">{priceValue > 0 ? `$${priceValue}` : 'Free'}</span>
            <button className="buy-button" onClick={handleBuy}>
              {priceValue > 0 ? 'Buy Now' : 'Play Now'}
            </button>
          </div>

          <hr />

          {/* Reviews List */}
          <div className="reviews-list">
            {reviews.map(r => (
              <div key={r.id} className="review-item">
                <strong>{'★'.repeat(r.rating) + '☆'.repeat(5 - r.rating)}</strong>
                <p>{r.comment}</p>
                <small>by User#{r.user_id}</small>
              </div>
            ))}
          </div>
        </div>

        <div className="detail-right">
          <img className="detail-screenshot" src={game.screenshotUrl} alt="Screenshot" />

          <div className="system-req">
            <h3>System Requirements</h3>
            <ul>
              <li>OS: Windows 10</li>
              <li>CPU: Quad-core</li>
              <li>RAM: 8 GB</li>
            </ul>
          </div>
        </div>
      </main>



      {/* Submit Review */}
      <section className="submit-review">
        <h4>Submit Your Review</h4>
        <form onSubmit={handleSubmitReview}>
          <div className="form-group">
            <label htmlFor="rating">Rating</label>
            <select
              id="rating"
              className="rating-select"
              value={rating}
              onChange={e => setRating(e.target.value)}
              required
            >
              <option value="">Select rating</option>
              <option value="5">5 - Excellent</option>
              <option value="4">4 - Good</option>
              <option value="3">3 - Average</option>
              <option value="2">2 - Poor</option>
              <option value="1">1 - Awful</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="review">Review</label>
            <textarea
              id="review"
              className="review-textarea"
              rows="5"
              placeholder="Write your review..."
              value={review}
              onChange={e => setReview(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="submit-btn">
            Submit Review
          </button>
        </form>
      </section>

      {/* Footer */}
      <footer className="detail-footer">
        <div className="footer-content">
          <ul>
            <li>About</li>
            <li>Support</li>
          </ul>
          <ul>
            <li>Privacy Policy</li>
            <li>Legal</li>
            <li>Cookies</li>
          </ul>
        </div>
      </footer>

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Thank you for your review!</p>
            <button onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
