import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Style/Library.css";

const Library = () => {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  // Lấy userId
  const storedUser = sessionStorage.getItem("user");
  const currentUser = storedUser ? JSON.parse(storedUser) : null;
  const userId = paramUserId || currentUser?.id;

  // Dữ liệu chính
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState("");

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const gamesPerPage = 10;

  // Fetch
  useEffect(() => {
    (async () => {
      setLoading(true);
      try {
        if (!userId) throw new Error("User ID missing");
        const res = await axios.get(`/api/users/${userId}/library`);
        const raw = Array.isArray(res.data) ? res.data : (res.data.items || []);
        // Giả định raw là array of { id, title, imageUrl }
        setGames(raw);
      } catch (e) {
        console.error(e);
        setGames([]);
      } finally {
        setLoading(false);
      }
    })();
  }, [userId]);

  if (loading) return <div className="loading">Loading...</div>;

  // Lọc theo từ khóa
  const filteredGames = games.filter(g => 
    g.title?.toLowerCase().includes(query.toLowerCase())
  );

  // Tính tổng trang trên filteredGames
  const totalPages = Math.ceil(filteredGames.length / gamesPerPage);

  // Lấy slice của trang hiện tại từ filteredGames
  const idxLast  = currentPage * gamesPerPage;
  const idxFirst = idxLast - gamesPerPage;
  const currentGames = filteredGames.slice(idxFirst, idxLast);

  // Handler chuyển trang
  const goToPage = (p) => {
    if (p < 1 || p > totalPages) return;
    setCurrentPage(p);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <>
      <header className="store-header">…</header>
      <h1>Your Library</h1>
      <div className="library-container">
        <div className="search-bar">
          <input
            placeholder="Search games..."
            value={query}
            onChange={e => {
              setQuery(e.target.value);
              setCurrentPage(1); // reset về trang 1 khi search
            }}
          />
        </div>

        {filteredGames.length === 0 ? (
          <div className="no-games">Không tìm thấy game nào.</div>
        ) : (
          <>
            <div className="library-games">
              {currentGames.map(game => (
                <div key={game.id} className="library-game-card">
                  <img src={game.imageUrl} alt={game.title}/>
                  <h3>{game.title}</h3>
                  <button className="play-button">Play</button>
                </div>
              ))}
            </div>

            {totalPages > 1 && (
              <div className="pagination">
                <button onClick={() => goToPage(currentPage - 1)}
                        disabled={currentPage === 1}>
                  Prev
                </button>
                {Array.from({ length: totalPages }, (_, i) => (
                  <button key={i+1}
                          onClick={() => goToPage(i+1)}
                          className={currentPage === i+1 ? "active" : ""}>
                    {i+1}
                  </button>
                ))}
                <button onClick={() => goToPage(currentPage + 1)}
                        disabled={currentPage === totalPages}>
                  Next
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </>
  );
};

export default Library;
