import axios from 'axios';

export const getGames = async () => {
  try {
    const response = await axios.get('http://localhost:5000/api/games');
    console.log('API response in getGames:', response.data);  // Kiểm tra dữ liệu trả về
    return response.data;  // Trả về mảng các game
  } catch (error) {
    console.error('Error fetching games:', error);
    throw error; // Đảm bảo lỗi được bắt
  }
};

// API call to add a review for a game
export const addReview = async (userId, gameId, rating, comment) => {
  try {
    const response = await axios.post('/api/reviews', {
      user_id: userId,
      game_id: gameId,
      rating: rating,
      comment: comment
    });
    return response.data;
  } catch (error) {
    console.error("There was an error submitting review!", error);
  }
};
