import React, { useState, useEffect } from 'react';
import { getStoreGames, getG} from './services/apiService';
import './Style/StorePage.css';

export default function StorePage() {
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);

  // pagination state per category
  const [pageInfo, setPageInfo] = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  useEffect(() => {
    // fetch featured (no pagination)
    getStoreGames()
      .then(res => setFeatured(res.items))
      .catch(err => console.error(err));

    // fetch each category with server pagination
    const fetchCategory = async (type, setter) => {
      try {
        const { items, total } = await getGames({ type, page: pageInfo[type], pageSize: 4 });
        setter(items);
        setTotalPages(prev => ({ ...prev, [type]: Math.ceil(total / 4) }));
      } catch (err) {
        console.error(err);
      }
    };

    fetchCategory('special', setSpecial);
    fetchCategory('free', setFreeGames);
    fetchCategory('best', setBestSellers);
  }, [pageInfo]);

  const changePage = (category, delta) => {
    setPageInfo(prev => {
      const next = prev[category] + delta;
      return { ...prev, [category]: Math.min(Math.max(1, next), totalPages[category]) };
    });
  };

  return (
    <div className="store-page">
      <header className="store-header">
        <span>STEAM</span>
        <nav>
          <a href="/">Home</a>
          <a href="/extras">Extras</a>
        </nav>
      </header>

      <div className="content-container">
        <div className="search-bar"><input placeholder="Tìm kiếm trò chơi..." /></div>

        {/* Featured Section */}
        <section className="featured-container">
          <h2>Featured</h2>
          <div className="featured">
            {featured.map(game => (
              <div key={game.id} className="featured-item">
                <img src={game.imageUrl} alt={game.title} />
                <div className="game-info"><h3>{game.title}</h3></div>
              </div>
            ))}
          </div>
        </section>

        {/* Special Offers */}
        <SectionWithPagination
          title="Special Offers"
          games={special}
          page={pageInfo.special}
          total={totalPages.special}
          onPrev={() => changePage('special', -1)}
          onNext={() => changePage('special', 1)}
        />

        {/* Free-to-play Games */}
        <SectionWithPagination
          title="Free-to-play Games"
          games={freeGames}
          page={pageInfo.free}
          total={totalPages.free}
          onPrev={() => changePage('free', -1)}
          onNext={() => changePage('free', 1)}
        />

        {/* Best Sellers */}
        <SectionWithPagination
          title="Best Sellers"
          games={bestSellers}
          page={pageInfo.best}
          total={totalPages.best}
          onPrev={() => changePage('best', -1)}
          onNext={() => changePage('best', 1)}
        />

        <footer className="footer">
          <div className="footer-content">
            <ul><li>About</li><li>Support</li></ul>
            <ul><li>Privacy Policy</li><li>Legal</li><li>Cookies</li></ul>
          </div>
        </footer>
      </div>
    </div>
  );
}

// Section with pagination helper
function SectionWithPagination({ title, games, page, total, onPrev, onNext }) {
  return (
    <section className={title === 'Best Sellers' ? 'best-seller' : title === 'Special Offers' ? 'special-offers' : 'free-games'}>
      <h2>{title}</h2>
      <div className="game-list">
        {games.map(game => <GameCard key={game.id} game={game} />)}
      </div>
      <div className="pagination pagination-bottom">
        <button disabled={page <= 1} onClick={onPrev}>{'< Prev'}</button>
        <span>{page}/{total}</span>
        <button disabled={page >= total} onClick={onNext}>{'Next >'}</button>
      </div>
    </section>
  );
}

// Reusable GameCard component
function GameCard({ game }) {
  const discounted = game.price * (1 - (game.discount || 0) / 100);

  return (
    <div className="game-card fixed-size">
      <div className="card-image-wrapper">
        {game.discount > 0 && <div className="discount">-{game.discount}%</div>}
        <img src={game.imageUrl} alt={game.title} />
      </div>
      <div className="card-content">
        <h3>{game.title}</h3>
        <div className="price-wrapper">
          {game.discount > 0 && <span className="original-price">${game.price.toFixed(2)}</span>}
          <span className="discounted-price">${discounted.toFixed(2)}</span>
        </div>
        <div className="sales-count">{game.salesCount.toLocaleString()} lượt mua</div>
      </div>
    </div>
  );
}
