// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Configure axios to point to backend server
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  const { gameId } = useParams();
  const navigate = useNavigate();

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [showModal, setShowModal] = useState(false);

  // Fetch game detail
  useEffect(() => {
    const fetchGame = async () => {
      try {
        console.log('Fetching game id:', gameId);
        const res = await axios.get(`/api/games/${gameId}`);
        console.log('Game data:', res.data);
        setGame(res.data);
      } catch (err) {
        console.error('Error fetching game detail:', err.response || err);
        if (err.response) {
          setError(`Error ${err.response.status}: ${err.response.data.error}`);
        } else {
          setError('Network or server error fetching game');
        }
      } finally {
        setLoading(false);
      }
    };
    fetchGame();
  }, [gameId]);

  // Fetch reviews for this game
  useEffect(() => {
    const fetchReviews = async () => {
      if (!game) return;
      try {
        console.log('Fetching reviews for game:', gameId);
        const res = await axios.get('/api/reviews', { params: { gameId } });
        console.log('Reviews data:', res.data);
        setReviews(res.data);
      } catch (err) {
        console.error('Error fetching reviews:', err.response || err);
      }
    };
    fetchReviews();
  }, [game, gameId]);

  // Submit a new review
  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/reviews', {
        userId: 1,
        gameId: game.id,
        comment,
        rating: Number(rating)
      });
      setShowModal(true);
      // Reload reviews
      const res = await axios.get('/api/reviews', { params: { gameId } });
      setReviews(res.data);
    } catch (err) {
      console.error('Error submitting review:', err.response || err);
      setError('Failed to submit review');
    }
  };

  if (loading) return <p>Loading game detail...</p>;
  if (error) return <p className="error">{error}</p>;
  if (!game) return <p className="error">Game data unavailable</p>;

  return (
    <div className="game-detail">
      <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Back</button>

      <div className="banner">
        <img src={game.bannerUrl || placeholderBanner} alt={game.title} />
      </div>

      <h1>{game.title}</h1>
      <p>{game.description}</p>

      <ul className="game-meta">
        <li><strong>Developer:</strong> {game.developer}</li>
        <li><strong>Publisher:</strong> {game.publisher}</li>
        <li><strong>Genre:</strong> {game.genre}</li>
        <li><strong>Price:</strong> ${game.price.toFixed(2)}</li>
        <li><strong>Sales:</strong> {game.salesCount.toLocaleString()}</li>
      </ul>

      <section className="reviews">
        <h2>Reviews</h2>
        {reviews.length === 0 && <p>No reviews yet.</p>}
        {reviews.map(r => (
          <div key={r.id} className="review-card">
            <strong>{r.user.name}</strong> rated {r.rating}/5
            <p>{r.comment}</p>
          </div>
        ))}
      </section>

      <section className="add-review">
        <h2>Add Your Review</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Rating:
            <select value={rating} onChange={e => setRating(e.target.value)} required>
              <option value="">Select</option>
              {[1, 2, 3, 4, 5].map(n => <option key={n} value={n}>{n}</option>)}
            </select>
          </label>
          <label>
            Comment:
            <textarea value={comment} onChange={e => setComment(e.target.value)} required />
          </label>
          <button type="submit">Submit Review</button>
        </form>
      </section>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Thank you for your review!</p>
            <button onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
