// StorePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured]       = useState([]);
  const [special, setSpecial]         = useState([]);
  const [freeGames, setFreeGames]     = useState([]);
  const [fullTopBest, setFullTopBest] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [games, setGames]             = useState([]);
  const [genres, setGenres]           = useState([{ id: 0, name: 'All' }]);
  const [selectedGenreId, setSelectedGenreId] = useState(0);

  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  const PAGE_SIZE = 4;
  const [pageInfo, setPageInfo]     = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  // chuyển giá string -> number
  function safePrice(val) {
    if (val == null || val === '') return null;
    let str = String(val).trim();
    if (/^\d{1,3}(\.\d{3})*,\d+$/.test(str)) {
      str = str.replace(/\./g, '').replace(',', '.');
    } else {
      const parts = str.split('.');
      if (parts.length > 2) {
        const decimal = parts.pop();
        const integer = parts.join('');
        str = integer + '.' + decimal;
      }
      str = str.replace(/,/g, '');
    }
    const num = Number(str);
    return Number.isFinite(num) ? num : null;
  }

  useEffect(() => {
    async function fetchData() {
      try {
        // 1) Featured, special, free (pageSize = PAGE_SIZE)
        const paramsBase = { page: 1, pageSize: PAGE_SIZE };
        const [resFeat, resSpec, resFree] = await Promise.all([
          axios.get('/api/games', { params: paramsBase }),
          axios.get('/api/games', { params: { ...paramsBase, type: 'special' } }),
          axios.get('/api/games', { params: { ...paramsBase, type: 'free' } }),
        ]);

        // 2) All best candidates (pageSize large), then sort & slice top16
        const resBestAll = await axios.get('/api/games', {
          params: { type: 'best', page: 1, pageSize: 1000 }
        });
        const sortedBest = (resBestAll.data.items || [])
          .sort((a,b)=> (b.salesCount||0)-(a.salesCount||0))
          .slice(0,16);

        // 3) All games (for search & genre filter)
        const resAll = await axios.get('/api/games', {
          params: { page: 1, pageSize: 1000 }
        });

        // 4) All genres
        const resGenres = await axios.get('/api/genres');
        const genreList = [{ id: 0, name: 'All' }, ...resGenres.data];

        // set state
        setFeatured(resFeat.data.items);
        setSpecial(resSpec.data.items);
        setFreeGames(resFree.data.items);
        setFullTopBest(sortedBest);
        setBestSellers(sortedBest.slice(0, PAGE_SIZE));
        setGames(resAll.data.items);
        setGenres(genreList);

        setTotalPages({
          special: Math.ceil(resSpec.data.total / PAGE_SIZE),
          free:    Math.ceil(resFree.data.total / PAGE_SIZE),
          best:    Math.ceil(sortedBest.length / PAGE_SIZE),
        });
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    }
    fetchData();
  }, []);

  // Prev/Next handler for special/free/best
  async function handlePageChange(category, delta) {
    const nextPage = Math.max(
      1,
      Math.min(pageInfo[category] + delta, totalPages[category])
    );
    setPageInfo(prev => ({ ...prev, [category]: nextPage }));

    if (category === 'best') {
      const start = (nextPage - 1) * PAGE_SIZE;
      setBestSellers(fullTopBest.slice(start, start + PAGE_SIZE));
      return;
    }

    try {
      const res = await axios.get('/api/games', {
        params: { type: category, page: nextPage, pageSize: PAGE_SIZE }
      });
      if (category === 'special') {
        setSpecial(res.data.items);
      } else if (category === 'free') {
        setFreeGames(res.data.items);
      }
    } catch (err) {
      console.error(`Error fetching ${category} page ${nextPage}:`, err);
    }
  }

  const handleDiscoverClick = () => navigate('/library');

  // Filter by search & genre
  let displayGames = games;
  if (query) {
    const q = query.toLowerCase();
    displayGames = displayGames.filter(g =>
      g.title.toLowerCase().includes(q)
    );
  }
  if (selectedGenreId !== 0) {
    displayGames = displayGames.filter(g => g.GenreID === selectedGenreId);
  }

  const activeGenreName = genres.find(g => g.id === selectedGenreId)?.name || '';

  return (
    <div className="store-page">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Logo" className="footer-logo"/>
          </span>
          <div className="user-actions">
            <button className="download-btn" onClick={handleDiscoverClick}>
              Library
            </button>
            <span className="cart-btn" onClick={()=>navigate('/cart')}>Cart</span>
            <span className="cart-btn" onClick={()=>navigate('/profile')}>Profile</span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="banner">
        <img
          src="/images/featured-banner.jpg"
          alt="Featured Banner"
          className="banner-img"
        />
      </div>

      {/* Search bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search games..."
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
      </div>

      {/* Genre filter */}
      <div className="genre-filter">
        {genres.map(genre => (
          <button
            key={genre.id}
            className={genre.id === selectedGenreId ? 'active' : ''}
            onClick={() => {
              setSelectedGenreId(genre.id);
              setQuery('');
            }}
          >
            {genre.name}
          </button>
        ))}
      </div>

      <div className="content-container">
        {/* If searching or filtering by genre */}
        {(query || selectedGenreId !== 0) ? (
          <section className="search-results">
            <h2>
              {query
                ? `Search Results for "${query}"`
                : `${activeGenreName} Games`}
            </h2>
            <div className="game-list">
              {displayGames.length ? (
                displayGames.map(game => (
                  <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                    <img src={game.imageUrl} alt={game.title}/>
                    <h3>{game.title}</h3>
                    {safePrice(game.price) > 0 ? (
                      <span>${(safePrice(game.price) ?? 0).toFixed(2)}</span>
                    ) : (
                      <span>Free</span>
                    )}
                  </Link>
                ))
              ) : (
                <p>No games found.</p>
              )}
            </div>
          </section>
        ) : (
          <>
            {/* Featured Game */}
            <section className="special-offers">
              <h2>Featured Game</h2>
              {featured[0] && (
                <div className="featured">
                  <Link to={`/game/${featured[0].id}`} className="game-card">
                    <img src={featured[0].imageUrl} alt={featured[0].title}/>
                  </Link>
                  <div className="game-info">
                    <h3>{featured[0].title}</h3>
                    <p>{featured[0].description}</p>
                    <p>{(featured[0].salesCount||0).toLocaleString()} lượt mua</p>
                  </div>
                </div>
              )}
            </section>

            {/* Special Deals */}
            <section className="special-offers">
              <h2>Special Deals</h2>
              <div className="game-list">
                {special.map(game => (
                  <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                    {game.discount>0 && <div className="discount">-{game.discount}%</div>}
                    <img src={game.imageUrl} alt={game.title}/>
                    <h3>{game.title}</h3>
                    {safePrice(game.price) > 0 && (
                      <span className="original-price">
                        ${(safePrice(game.price) ?? 0).toFixed(2)}
                      </span>
                    )}
                    <span className="discounted-price">
                      ${((safePrice(game.price) ?? 0)*(1-(game.discount??0)/100)).toFixed(2)}
                    </span>
                    <p>{(game.salesCount||0).toLocaleString()} lượt mua</p>
                  </Link>
                ))}
              </div>
              <div className="pagination">
                <button onClick={()=>handlePageChange('special',-1)} disabled={pageInfo.special===1}>&lt; Prev</button>
                <span>{pageInfo.special}/{totalPages.special}</span>
                <button onClick={()=>handlePageChange('special',1)} disabled={pageInfo.special===totalPages.special}>Next &gt;</button>
              </div>
            </section>

            {/* Free Games */}
            <section className="free-games">
              <h2>Free Games</h2>
              <div className="game-list">
                {freeGames.map(game=>(
                  <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                    {game.discount>0 && <div className="discount">-{game.discount}%</div>}
                    <img src={game.imageUrl} alt={game.title}/>
                    <h3>{game.title}</h3>
                    <p>Free</p>
                    <p>{(game.salesCount||0).toLocaleString()} lượt mua</p>
                  </Link>
                ))}
              </div>
              <div className="pagination">
                <button onClick={()=>handlePageChange('free',-1)} disabled={pageInfo.free===1}>&lt; Prev</button>
                <span>{pageInfo.free}/{totalPages.free}</span>
                <button onClick={()=>handlePageChange('free',1)} disabled={pageInfo.free===totalPages.free}>Next &gt;</button>
              </div>
            </section>

            {/* Best Sellers */}
            <section className="best-seller">
              <h2>Best Sellers</h2>
              <div className="game-list">
                {bestSellers.map(game=>(
                  <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                    <div className="discount">-{game.discount}%</div>
                    <img src={game.imageUrl} alt={game.title}/>
                    <h3>{game.title}</h3>
                    {safePrice(game.price)>0 && (
                      <span className="original-price">
                        ${(safePrice(game.price) ?? 0).toFixed(2)}
                      </span>
                    )}
                    <span className="discounted-price">
                      ${((safePrice(game.price) ?? 0)*(1-(game.discount??0)/100)).toFixed(2)}
                    </span>
                    <p>{(game.salesCount||0).toLocaleString()} lượt mua</p>
                  </Link>
                ))}
              </div>
              <div className="pagination">
                <button onClick={()=>handlePageChange('best',-1)} disabled={pageInfo.best===1}>&lt; Prev</button>
                <span>{pageInfo.best}/{totalPages.best}</span>
                <button onClick={()=>handlePageChange('best',1)} disabled={pageInfo.best===totalPages.best}>Next &gt;</button>
              </div>
            </section>
          </>
        )}
      </div>

      {/* Footer unchanged */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo"/>
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners in the US and other countries.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li><li>Jobs</li><li>Steamworks</li><li>Steam Distribution</li><li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li><li>Legal</li><li>Steam Subscriber Agreement</li><li>Refunds</li><li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
