import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from "react-router-dom";
import axios from "axios";

import Login from "./Login";
import StorePage from "./StorePage";
import GameDetail from "./GameDetail";
import Cart from "./Cart";
import Library from "./Library";
import Profile from "./Profile";
import AddGame from "./AddGame";
import Checkout from "./Checkout";

// Thiết lập URL backend chung
axios.defaults.baseURL = process.env.REACT_APP_API_URL || "http://localhost:5000";

function App() {

  const [user, setUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);

  // Hàm xử lý khi đăng nhập thành công
  const handleLogin = (userData, token) => {
    // Lưu xuống localStorage để khỏi mất khi refresh
    localStorage.setItem("token", token);
    localStorage.setItem("user", JSON.stringify(userData));

    // Gắn header mặc định cho axios
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    // Cập nhật state
    setUser(userData);
  };

  // Load lại thông tin đăng nhập khi F5
  useEffect(() => {
    const savedToken = localStorage.getItem("token");
    const savedUser = localStorage.getItem("user");

    if (savedToken && savedUser) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${savedToken}`;
      setUser(JSON.parse(savedUser));
    }
  }, []);

  // Xử lý logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    delete axios.defaults.headers.common["Authorization"];
    setUser(null);
    setCartItems([]);
  };

  return (
    <Router>
      <Routes>
        {/* Login */}
        <Route
          path="/login"
          element={
            user ? (
              <Navigate to="/store" replace />
            ) : (
              <Login onLogin={handleLogin} />
            )
          }
        />

        {/* Trang chủ */}
        <Route
          path="/"
          element={
            user ? (
              <StorePage
                cartItems={cartItems}
                setCartItems={setCartItems}
                user={user}
                onLogout={handleLogout}
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Store */}
        <Route
          path="/store"
          element={
            user ? (
              <StorePage
                cartItems={cartItems}
                setCartItems={setCartItems}
                user={user}
                onLogout={handleLogout}
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Game detail */}
        <Route
          path="/game/:gameId"
          element={
            user ? <GameDetail user={user} /> : <Navigate to="/login" replace />
          }
        />

        {/* Cart */}
        <Route
          path="/cart"
          element={
            user ? (
              <Cart
                cartItems={cartItems}
                setCartItems={setCartItems}
                user={user}
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Library */}
        <Route
          path="/library"
          element={user ? <Library user={user} /> : <Navigate to="/login" replace />}
        />

        {/* Profile */}
        <Route
          path="/profile"
          element={user ? <Profile user={user} /> : <Navigate to="/login" replace />}
        />

        {/* Add game (admin) */}
        <Route
          path="/addgame"
          element={user ? <AddGame user={user} /> : <Navigate to="/login" replace />}
        />

        {/* Checkout */}
        <Route
          path="/checkout"
          element={
            user ? (
              <Checkout setCartItems={setCartItems} user={user} />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Fallback */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
