import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import Review from "./Review"; // Component xử lý form + list review
import "./Style/GameDetail.css";

// Dữ liệu mẫu
const GAME_DATA = {
  "elden-ring": {
    title: "Elden Ring",
    price: "$59.99",
    description: "A fantasy action RPG by FromSoftware and Bandai Namco.",
    headerImg: "https://cdn.akamai.steamstatic.com/steam/apps/1245620/header.jpg",
    screenshot: "/elden_ring_sc.jpg",
    releaseDate: "Feb 24, 2022",
    developer: "FromSoftware Inc.",
    publisher: "Bandai Namco",
    systemReq:
      "OS: Windows 10 | Processor: INTEL CORE I5-8400 | Memory: 12 GB RAM",
    genre: "Action RPG",
  },
  // ... các game khác
};

const GameDetail = ({ cartItems, setCartItems, userId }) => {
  const navigate = useNavigate();
  const { gameId } = useParams();
  const game = GAME_DATA[gameId];

  const [justAdded, setJustAdded] = useState(false);
  const [reviews, setReviews] = useState([]);

  const [rating, setRating] = useState(5);          // mặc định 5 sao
  const [reviewText, setReviewText] = useState(""); // nội dung đánh giá

  // Fetch reviews khi gameId thay đổi
  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const res = await axios.get(`/api/reviews/${gameId}`);
        setReviews(res.data);
      } catch (err) {
        console.error("Error fetching reviews:", err);
      }
    };
    if (game) {
      fetchReviews();
    }
  }, [gameId, game]);

  // Nếu không có game tương ứng, hiển thị not-found
  if (!game) {
    return (
      <div className="detail-not-found">
        <h2>Game not found</h2>
        <button onClick={() => navigate("/store")}>Back to Store</button>
      </div>
    );
  }

  const handleBuy = (e) => {
    e.stopPropagation();
    setCartItems((prev) => {
      if (prev.some((item) => item.name === game.title)) return prev;
      return [...prev, { name: game.title, price: game.price }];
    });
    setJustAdded(true);
    setTimeout(() => setJustAdded(false), 3000);
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    // đơn giản: kiểm tra rỗng
    if (!reviewText.trim()) return;

    try {
      // gọi API POST (tuỳ backend của bạn)
      await axios.post(`/api/reviews/${gameId}`, {
        rating,
        text: reviewText,
        author: "Ẩn danh"          // hoặc tên user đăng nhập
      });

      // lấy lại danh sách review để cập nhật UI
      const res = await axios.get(`/api/reviews/${gameId}`);
      setReviews(res.data);

      // reset form
      setRating(5);
      setReviewText("");
    } catch (err) {
      console.error("Gửi đánh giá thất bại:", err);
    }
  };

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span
            className="steam-logo"
            onClick={() => navigate("/store")}
            style={{ cursor: "pointer" }}
          >
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
          </span>
          <div className="user-actions">
            <button onClick={() => navigate("/library")}>Library</button>
            <span>Wishlist</span>
            <span onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>
              Cart
            </span>
            <span onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={game.headerImg} alt={game.title} />
      </div>

      {/* Main */}
      <section className="detail-main">
        <div className="detail-left">
          <h2>{game.title}</h2>
          <div className="detail-buy">
            <span className="price">{game.price}</span>
            <button className="buy-button" onClick={handleBuy}>
              {game.price.toLowerCase().includes("free") ? "Play Now" : "Add to Cart"}
            </button>
          </div>
          <p className="description">{game.description}</p>
          <p>
            <strong>Genre:</strong> {game.genre}
          </p>
          <hr />
          <p>
            <strong>Release Date:</strong> {game.releaseDate}
          </p>
          <p>
            <strong>Developer:</strong> {game.developer}
          </p>
          <p>
            <strong>Publisher:</strong> {game.publisher}
          </p>
        </div>

        <div className="detail-right">
          <img
            src={game.screenshot}
            alt={`${game.title} screenshot`}
            className="detail-screenshot"
          />
          <div className="system-req">
            <h3>System Requirements</h3>
            <p>{game.systemReq}</p>
          </div>
        </div>
      </section>

      {/* Modal khi vừa thêm vào giỏ */}
      {justAdded && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Added to cart!</p>
            <button onClick={() => setJustAdded(false)}>Close</button>
          </div>
        </div>
      )}

      {/* LIST REVIEWS */}
{reviews.length === 0 ? (
  <p style={{ textAlign: "center", color: "#d7dfe7" }}>Chưa có đánh giá nào.</p>
) : (
  reviews.map((rv, i) => (
    <div key={i} className="review-card">
      <p>{rv.text}</p>
      <div className="review-meta">
        <span className="review-author">{rv.author}</span>
        <span className="review-rating">{rv.rating} ★</span>
      </div>
    </div>
  ))
)}

{/* FORM GỬI REVIEW */}
<div className="submit-review">
  <h4>Gửi đánh giá của bạn</h4>
  <form onSubmit={handleSubmit}>
    <div className="form-group">
      <label htmlFor="rating">Chọn số sao</label>
      <select
        id="rating"
        className="rating-select"
        value={rating}
        onChange={e => setRating(e.target.value)}
      >
        {[5,4,3,2,1].map(star => (
          <option key={star} value={star}>{star} ★</option>
        ))}
      </select>
    </div>

    <div className="form-group">
      <label htmlFor="reviewText">Nội dung đánh giá</label>
      <textarea
        id="reviewText"
        className="review-textarea"
        value={reviewText}
        onChange={e => setReviewText(e.target.value)}
      />
    </div>

    <button className="submit-btn" type="submit">Gửi đánh giá</button>
  </form>
</div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US and
              other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon twitter"></div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default GameDetail;
