// Cart.js
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Cart.css';

// Thiết lập URL backend chung
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Cart({ user, cartItems, setCartItems }) {
  const navigate = useNavigate();

  // Chuyển cartItems thành mảng an toàn
  const items = Array.isArray(cartItems) ? cartItems : [];

  useEffect(() => {
    async function fetchCartItems() {
      try {
        const stored = JSON.parse(localStorage.getItem('user'));
        const userId = stored?.id;
        const res = userId
          ? await axios.get(`/api/cart?userId=${userId}`)
          : await axios.get('/api/cart/me');
        console.log('Cart API response:', res.data);
        // Chuẩn hóa dữ liệu tùy theo shape API
        const data = Array.isArray(res.data)
          ? res.data
          : Array.isArray(res.data.items)
            ? res.data.items
            : Array.isArray(res.data.cartItems)
              ? res.data.cartItems
              : [];
        setCartItems(data);
      } catch (err) {
        console.error('Fetch cart failed:', err);
      }
    }
    fetchCartItems();
  }, [setCartItems]);

  const handleRemove = async (cartItemId) => {
    try {
      await axios.delete(`/api/cart/${cartItemId}`);
      const updated = items.filter(item => item.id !== cartItemId);
      setCartItems(updated);
    } catch (err) {
      console.error('Remove failed:', err);
    }
  };

  const total = items.reduce((sum, itm) => {
    const price = Number(itm.Price) || 0;
    const discount = Number(itm.Discount) || 0;
    return sum + price * (1 - discount / 100);
  }, 0);

  const handleCheckout = () => navigate('/checkout');

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {items.length > 0 ? (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Game</th>
                <th>Price</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item.id}>
                  <td>{item.Name}</td>
                  <td>{(item.Price * (1 - item.Discount/100)).toFixed(2)}</td>
                  <td>
                    <button onClick={() => handleRemove(item.id)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="cart-summary">
            <p>Total: ${total.toFixed(2)}</p>
          </div>

          <div className="cart-actions">
            <button className="btn-back" onClick={() => navigate('/store')}>Continue Shopping</button>
            <button className="btn-checkout" onClick={handleCheckout}>Checkout</button>
          </div>
        </>
      ) : (
        <p>Your cart is empty.</p>
      )}
    </div>
  );
}
