import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/StorePage.css";

// Khởi tạo axios baseURL qua biến môi trường hoặc mặc định
const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
axios.defaults.baseURL = API_BASE;

const StorePage = ({ cartItems = [] }) => {
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGames = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.get("/api/games", { params: { search: searchTerm } });
        setGames(res.data);
      } catch (err) {
        console.error("Fetch games error:", err);
        setError(err.response ? `${err.response.status} ${err.response.statusText}` : "Không thể kết nối server");
      } finally {
        setLoading(false);
      }
    };
    const timer = setTimeout(fetchGames, 300);
    return () => clearTimeout(timer);
  }, [searchTerm]);

  return (
    <>
      <header className="store-header">
        <div className="nav">
          <span>Valve Steam</span>
          <div className="user-actions">
            <button className="download-btn" onClick={() => navigate("/library")}>Library</button>
            <span onClick={() => navigate("/wishlist")} style={{ cursor: "pointer" }}>Wishlist</span>
            <span onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>Cart ({cartItems.length})</span>
          </div>
        </div>
      </header>

      <div className="banner">
        <img src="/summer_sale.jpg" alt="Summer Sale Banner" />
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      <section className="search-results">
        {loading && <p className="loading">Đang tải trò chơi...</p>}
        {error && <p className="error">Lỗi: {error}</p>}
        {!loading && !error && games.length === 0 && <p className="no-results">Không tìm thấy trò chơi nào.</p>}
        <div className="game-list">
          {games.map(game => (
            <div key={game.key} className="game-card" onClick={() => navigate(`/game/${game.key}`)}>
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              <span className="price">{game.price}</span>
              <button className="buy-now" onClick={e => { e.stopPropagation(); navigate(`/game/${game.key}`); }}>
                Xem chi tiết
              </button>
            </div>
          ))}
        </div>
      </section>

      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" className="footer-logo" alt="Logo" />
            <p>© {new Date().getFullYear()} Valve Corporation. All rights reserved.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>Về chúng tôi</li>
              <li>Hỗ trợ</li>
              <li>Liên hệ</li>
            </ul>
            <ul>
              <li>Quyền riêng tư</li>
              <li>Điều khoản</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </>
  );
};

export default StorePage;
