import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Style/Library.css";

const Library = () => {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  // Lấy userId từ session hoặc param URL
  const storedUser = sessionStorage.getItem("user");
  const parsedUser = storedUser ? JSON.parse(storedUser) : null;
  const currentUserId = parsedUser?.id;
  const userId = paramUserId || currentUserId;

  // State chính
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState("");

  // *** Pagination state và constant phải ở đây ***
  const [currentPage, setCurrentPage] = useState(1);
  const gamesPerPage = 10;

  // Fetch library data
  useEffect(() => {
    const fetchLibrary = async () => {
      setLoading(true);
      try {
        if (!userId) throw new Error("User ID is missing");
        let res = await axios.get(`/api/users/${userId}/library`)
          .catch(async err => {
            if (err.response?.status === 404) {
              try { return await axios.get(`/api/users/${userId}/games`); }
              catch { return await axios.get(`/api/library/${userId}`); }
            }
            throw err;
          });

        const data = res.data;
        let rawItems = Array.isArray(data) ? data : (data.items || data.data || data.library || data.games || []);
        // Nếu chỉ có ID, fetch chi tiết
        const ids = rawItems.map(it => it.id ?? it.gameId ?? it).filter(Boolean);
        if (ids.length && !rawItems[0].title) {
          const details = await Promise.all(ids.map(id => axios.get(`/api/games/${id}`)));
          setGames(details.map(r => r.data));
        } else {
          setGames(rawItems);
        }
      } catch (error) {
        console.error("Error fetching library:", error);
        setGames([]);
      } finally {
        setLoading(false);
      }
    };
    fetchLibrary();
  }, [userId]);

  if (loading) {
    return <div className="loading">Loading games for user {userId}...</div>;
  }

  // Lọc theo query
  const filteredGames = games.filter(game =>
    game.title?.toLowerCase().includes(query.toLowerCase())
  );

  // Tính tổng số trang dựa trên filteredGames
  const totalPages = Math.ceil(filteredGames.length / gamesPerPage);

  // Lấy slice của trang hiện tại
  const indexOfLastGame  = currentPage * gamesPerPage;
  const indexOfFirstGame = indexOfLastGame - gamesPerPage;
  const currentGames     = filteredGames.slice(indexOfFirstGame, indexOfLastGame);

  // Chuyển trang
  const goToPage = page => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <>
      {/* HEADER */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

      <h1>Your Library</h1>
      <div className="library-container">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search games..."
            value={query}
            onChange={e => {
              setQuery(e.target.value);
              setCurrentPage(1); // reset về trang 1 khi search
            }}
          />
        </div>

        {filteredGames.length === 0 ? (
          <div className="no-games">Không tìm thấy game nào.</div>
        ) : (
          <div className="library-games">
            {currentGames.map(game => (
              <div key={game.id} className="library-game-card">
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <button className="play-button" onClick={() => { /* play logic */ }}>
                  Play
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Pagination controls */}
        {totalPages > 1 && (
          <div className="pagination">
            <button onClick={() => goToPage(currentPage - 1)} disabled={currentPage === 1}>
              Prev
            </button>
            {Array.from({ length: totalPages }, (_, i) => (
              <button
                key={i + 1}
                onClick={() => goToPage(i + 1)}
                className={currentPage === i + 1 ? "active" : ""}
              >
                {i + 1}
              </button>
            ))}
            <button onClick={() => goToPage(currentPage + 1)} disabled={currentPage === totalPages}>
              Next
            </button>
          </div>
        )}
        <div>Page: {currentPage} / {totalPages}, Showing {currentGames.length} items</div>

      </div>
    </>
  );
};

export default Library;
