import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const GameDetail = ({ userId }) => {
  // Lấy gameId từ URL params và chuyển về số
  const { gameId } = useParams();
  const id = parseInt(gameId, 10);

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [reviewText, setReviewText] = useState('');

  // Nếu id không phải số, hiển thị lỗi
  useEffect(() => {
    if (isNaN(id)) {
      console.error('Invalid game ID:', gameId);
      return;
    }

    const fetchGameDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/games/${id}`);
        setGame(response.data);
      } catch (error) {
        console.error('Error fetching game details:', error);
      }
    };

    fetchGameDetails();
  }, [id, gameId]);

  // Submit a new review
  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (isNaN(id)) return;

    try {
      const response = await axios.post(
        `http://localhost:5000/api/games/${id}/reviews`,
        {
          userId,
          rating: Number(rating),
          comment: reviewText,
        }
      );
      setReviews([...reviews, response.data]);
      setReviewText('');
      setRating(5);
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  if (!game) return <div>Loading...</div>;

  return (
    <div>
      <h2>{game.name}</h2>
      <p>{game.description}</p>
      <p>Price: ${game.price}</p>
      {game.imageUrl && <img src={game.imageUrl} alt={game.name} />}

      <h3>Reviews</h3>
      {reviews.length > 0 ? (
        reviews.map((review) => (
          <div key={review.id}>
            <p>Rating: {review.rating} stars</p>
            <p>{review.comment}</p>
          </div>
        ))
      ) : (
        <p>No reviews yet.</p>
      )}

      <h3>Submit your review</h3>
      <form onSubmit={handleSubmitReview}>
        <div>
          <label>Rating: </label>
          <select value={rating} onChange={(e) => setRating(e.target.value)}>
            {[1, 2, 3, 4, 5].map((star) => (
              <option key={star} value={star}>
                {star} Star{star > 1 ? 's' : ''}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Review: </label>
          <textarea
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
            rows="4"
            cols="50"
            placeholder="Write your review here"
          />
        </div>
        <button type="submit">Submit Review</button>
      </form>
    </div>
  );
};

export default GameDetail;
