import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Style/GameDetail.css';  // Import CSS styling

const GameDetail = ({ userId }) => {
  const { gameId } = useParams();
  const initialId = parseInt(gameId, 10);

  const [game, setGame] = useState(null);
  const [numericId, setNumericId] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [reviewText, setReviewText] = useState('');

  // Chuyển tên game thành slug
  const toSlug = (str) =>
    str.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]+/g, '');

  useEffect(() => {
    const fetchGameDetails = async () => {
      try {
        let fetchedGame;
        if (!isNaN(initialId)) {
          const response = await axios.get(`http://localhost:5000/api/games/${initialId}`);
          fetchedGame = response.data;
        } else {
          const listRes = await axios.get('http://localhost:5000/api/games');
          const allGames = listRes.data;
          fetchedGame = allGames.find((g) => toSlug(g.name) === gameId);
          if (!fetchedGame) {
            console.error('Game not found for slug:', gameId);
            return;
          }
        }
        setGame(fetchedGame);
        setNumericId(fetchedGame.id);
      } catch (error) {
        console.error('Error fetching game details:', error);
      }
    };
    fetchGameDetails();
  }, [gameId, initialId]);

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (numericId == null) return;
    try {
      const response = await axios.post(
        `http://localhost:5000/api/games/${numericId}/reviews`,
        { userId, rating: Number(rating), comment: reviewText }
      );
      setReviews([...reviews, response.data]);
      setReviewText('');
      setRating(5);
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  if (!game) return <div className="loading">Loading...</div>;

  return (
    <div className="game-detail-container">
      <h2 className="game-title">{game.name}</h2>
      <p className="game-description">{game.description}</p>
      <p className="game-price">Price: ${game.price}</p>
      {game.imageUrl && <img className="game-image" src={game.imageUrl} alt={game.name} />}

      <section className="reviews-section">
        <h3>Reviews</h3>
        {reviews.length > 0 ? (
          <div className="reviews-list">
            {reviews.map((review) => (
              <div key={review.id} className="review-item">
                <p className="review-rating">Rating: {review.rating} stars</p>
                <p className="review-comment">{review.comment}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="no-reviews">No reviews yet.</p>
        )}
      </section>

      <section className="review-form-section">
        <h3>Submit your review</h3>
        <form className="review-form" onSubmit={handleSubmitReview}>
          <div className="form-group">
            <label htmlFor="rating">Rating:</label>
            <select
              id="rating"
              className="rating-select"
              value={rating}
              onChange={(e) => setRating(e.target.value)}
            >
              {[1, 2, 3, 4, 5].map((star) => (
                <option key={star} value={star}>
                  {star} Star{star > 1 ? 's' : ''}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="comment">Review:</label>
            <textarea
              id="comment"
              className="comment-textarea"
              value={reviewText}
              onChange={(e) => setReviewText(e.target.value)}
              rows="4"
              placeholder="Write your review here"
            />
          </div>

          <button type="submit" className="submit-button">
            Submit Review
          </button>
        </form>
      </section>
    </div>
  );
};

export default GameDetail;
