import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "./Style/Profile.css";

const Profile = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [uploadedGames, setUploadedGames] = useState([]);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    // Nếu user đã xác thực, lấy thêm dữ liệu bổ sung
    axios.defaults.headers.common["Authorization"] = `Bearer ${sessionStorage.getItem("token")}`;
    const userId = user.id || user.userId || user._id;

    // Fetch uploaded games
    axios.get(`/api/games?uploader=${userId}`)
      .then(res => {
        const payload = res.data;
        let list = [];
        if (Array.isArray(payload)) list = payload;
        else if (Array.isArray(payload.data)) list = payload.data;
        else if (Array.isArray(payload.games)) list = payload.games;
        setUploadedGames(list);
      })
      .catch(err => console.error("Error fetching games:", err));

    // Fetch user reviews
    axios.get(`/api/users/${userId}/reviews`)
      .then(res => {
        const payload = res.data;
        let list = [];
        if (Array.isArray(payload)) list = payload;
        else if (Array.isArray(payload.data)) list = payload.data;
        else if (Array.isArray(payload.reviews)) list = payload.reviews;
        setReviews(list);
      })
      .catch(err => console.error("Error fetching reviews:", err));

  }, [user, navigate]);

  if (!user) {
    return <div className="loading">Loading profile…</div>;
  }

  return (
    <div className="profile-page">
      <header>
        <nav className="nav-bar">
          <Link to="/library" className="nav-link">Discover</Link>
          <Link to="/profile" className="nav-link">Profile</Link>
        </nav>
      </header>

      <main className="profile-container">
        {/* HEADER */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

        {/* Recent Activity */}
        <section className="recent-activity">
          <h3>Recent Activity</h3>
          {Array.isArray(user.recentActivity) && user.recentActivity.length > 0
            ? <div className="activity-list">
                {user.recentActivity.map((item, i) => (
                  <div key={i} className="activity-item">
                    <img src={item.image} alt={item.game} />
                    <p>{item.game}</p>
                    <p>{item.hours} hrs</p>
                    <p>Last played: {item.lastPlayed}</p>
                  </div>
                ))}
              </div>
            : <p className="placeholder-text">No recent activity.</p>
          }
        </section>

        {/* Uploaded Games */}
        <section className="uploaded-games">
          <h3>Uploaded Games</h3>
          {uploadedGames.length > 0
            ? <div className="uploaded-list">
                {uploadedGames.map(game => (
                  <div key={game.id} className="game-card">
                    <img src={game.imageUrl} alt={game.title} />
                    <h4>{game.title}</h4>
                    <p>{game.price}</p>
                    <button onClick={() => navigate(`/games/${game.id}`)}>View</button>
                  </div>
                ))}
              </div>
            : <p className="placeholder-text">You haven’t uploaded any games yet.</p>
          }
        </section>

        <button className="logout-btn" onClick={onLogout}>
          Đăng xuất
        </button>
      </main>
    </div>
  );
};

export default Profile;
