import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Style/Login.css";

const Login = () => {
  const navigate = useNavigate();
  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      const user = await loginUser(email, password);
      if (!user || !user.id) throw new Error();
      onLogin(user.id);
    } catch {
      setError('Đăng nhập thất bại. Vui lòng kiểm tra lại email và mật khẩu.');
    }
  };

  const handleLogin = () => {
    if (loginData.username && loginData.password) {
      navigate("/store");
    } else {
      alert("Vui lòng nhập tài khoản và mật khẩu!");
    }
  };

  const handleRegister = () => {
    if (
      registerData.email &&
      registerData.confirmEmail &&
      registerData.steamUsername &&
      registerData.newPassword &&
      registerData.confirmPassword &&
      registerData.numBank
    ) {
      if (registerData.email !== registerData.confirmEmail) {
        alert("Email xác nhận không trùng khớp!");
        return;
      }
      if (registerData.newPassword !== registerData.confirmPassword) {
        alert("Mật khẩu xác nhận không trùng khớp!");
        return;
      }
      alert("Đăng ký thành công!");
    } else {
      alert("Vui lòng điền đầy đủ thông tin đăng ký!");
    }
  };

  return (
    <div className="container">
      {/* Header */}
      <header className="store-header">
        <img
          src="https://store.steampowered.com/public/shared/images/header/logo_steam.svg?t=962016"
          alt="Steam Logo"
          className="logo"
        />
      </header>

      {/* Auth Box */}
      <div className="auth-container">
        {/* Login */}
        <div className="auth-box">
          <h2>Đăng nhập</h2>
          <input
            type="text"
            placeholder="Tên tài khoản"
            value={loginData.username}
            onChange={(e) =>
              setLoginData({ ...loginData, username: e.target.value })
            }
          />
          <input
            type="password"
            placeholder="Mật khẩu"
            value={loginData.password}
            onChange={(e) =>
              setLoginData({ ...loginData, password: e.target.value })
            }
          />
          <button onClick={handleLogin}>Đăng nhập</button>
        </div>

        {/* Register */}
        <div className="auth-box">
          <h2>Tạo tài khoản của bạn</h2>
          <input
            type="email"
            placeholder="Địa chỉ email"
            value={registerData.email}
            onChange={(e) =>
              setRegisterData({ ...registerData, email: e.target.value })
            }
          />
          <input
            type="email"
            placeholder="Xác nhận địa chỉ của bạn"
            value={registerData.confirmEmail}
            onChange={(e) =>
              setRegisterData({
                ...registerData,
                confirmEmail: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Tên tài khoản Steam"
            value={registerData.steamUsername}
            onChange={(e) =>
              setRegisterData({
                ...registerData,
                steamUsername: e.target.value,
              })
            }
          />
          <input
            type="text"
            placeholder="Số tài khoản ngân hàng"
            value={registerData.numBank}
            onChange={(e) =>
              setRegisterData({
                ...registerData,
                numBank: e.target.value,
              })
            }
          />
          <input
            type="password"
            placeholder="Chọn mật khẩu"
            value={registerData.newPassword}
            onChange={(e) =>
              setRegisterData({
                ...registerData,
                newPassword: e.target.value,
              })
            }
          />
          <input
            type="password"
            placeholder="Xác nhận mật khẩu"
            value={registerData.confirmPassword}
            onChange={(e) =>
              setRegisterData({
                ...registerData,
                confirmPassword: e.target.value,
              })
            }
          />
          <button onClick={handleRegister}>Hoàn tất</button>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners in the US and other countries.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Login;
