// src/StorePage.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/StorePage.css";

// Thiết lập axios baseURL
const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
axios.defaults.baseURL = API_BASE;

const StorePage = ({ cartItems = [] }) => {
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGames = async () => {
      setLoading(true);
      setError(null);
      try {
        const { data } = await axios.get("/api/games", { params: { search: searchTerm } });
        setGames(data);
      } catch (err) {
        console.error("Error fetching games:", err);
        setError(err.response ? `${err.response.status} ${err.response.statusText}` : "Server error");
      } finally {
        setLoading(false);
      }
    };
    fetchGames();
  }, [searchTerm]);

  // Lấy các nhóm game
  const featured = games[0] || null;
  const specialOffers = games.filter(g => Number(g.discount) > 0);
  const freeGames = games.filter(g => Number(g.price) === 0);
  const bestSellers = games.slice(0, 5);
  const handleDiscoverClick = () => {
    navigate('/library');
  };
  return (
    <div className="store-page">
      {/* Header & banner */}
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate("/store")}>
          <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
          </span>
          <div className="user-actions">
          <button className="download-btn" onClick={handleDiscoverClick}>
                Library
            </button>
            <span>Wishlist</span>
            <span className="cart-btn" onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Search */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      {loading && <p className="loading">Đang tải...</p>}
      {error   && <p className="error">Lỗi: {error}</p>}

      {/* Featured */}
      {featured && (
        <section className="featured-container">
          <h2>FEATURED</h2>
          <div className="featured">
            <div
              className="featured-item"
              onClick={() => navigate(`/game/${featured.id}`)} // dùng id thay vì key
            >
              <img src={featured.imageUrl} alt={featured.title} />
              <div className="game-info">
                <h3 className="featured-title">{featured.title}</h3>
                <p>{featured.description}</p>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Special Offers */}
      {specialOffers.length > 0 && (
        <section>
          <h2>Special Offers</h2>
          <div className="game-list">
            {specialOffers.map(game => (
              <div
                key={game.id}
                className="game-card"
                onClick={() => navigate(`/game/${game.id}`)} // dùng id
              >
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="discount">-{game.discount}%</span>
                <span className="price">{typeof game.price === 'number' ? `$${game.price}` : game.price}</span>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Free-to-Play Games */}
      {freeGames.length > 0 && (
        <section>
          <h2>Free-to-Play Games</h2>
          <div className="game-list">
            {freeGames.map(game => (
              <div
                key={game.id}
                className="game-card"
                onClick={() => navigate(`/game/${game.id}`)} // dùng id
              >
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">Free</span>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Best Sellers */}
      {bestSellers.length > 0 && (
        <section>
          <h2>Best Sellers</h2>
          <div className="game-list">
            {bestSellers.map(game => (
              <div
                key={game.id}
                className="game-card"
                onClick={() => navigate(`/game/${game.id}`)} // dùng id
              >
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">{typeof game.price === 'number' ? `$${game.price}` : game.price}</span>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img
              src="/logo.png"
              alt="Valve Steam Logo"
              className="footer-logo"
            />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US
              and other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default StorePage;
