import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [isRegister, setIsRegister] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    accountNumberBank: ''
  });
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      const url = isRegister
        ? 'http://localhost:5000/api/auth/register'
        : 'http://localhost:5000/api/auth/login';

      const payload = isRegister
        ? {
            name: formData.name,
            email: formData.email,
            password: formData.password,
            accountNumberBank: formData.accountNumberBank
          }
        : {
            email: formData.email,
            password: formData.password
          };

      const res = await axios.post(url, payload, {
        headers: { 'Content-Type': 'application/json' }
      });

      setUser(res.data);
      // Sau khi đăng nhập/đăng ký thành công, chuyển sang trang Store
      navigate('/store');
    } catch (err) {
      setError(err.response?.data?.error || 'Something went wrong');
    }
  };

  return (
    <div className="login-container">
      <h2>{isRegister ? 'Đăng ký' : 'Đăng nhập'}</h2>

      {user && (
        <div className="welcome">
          <p>Xin chào, <strong>{user.name || user.email}</strong>!</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="login-form">
        {isRegister && (
          <>
            <label>Họ & Tên</label>
            <input
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <label>Số tài khoản</label>
            <input
              name="accountNumberBank"
              value={formData.accountNumberBank}
              onChange={handleChange}
              required
            />
          </>
        )}

        <label>Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <label>Mật khẩu</label>
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          required
        />

        {error && <p className="error-message">{error}</p>}

        <button type="submit" className="btn">
          {isRegister ? 'Đăng ký' : 'Đăng nhập'}
        </button>
      </form>

      <p className="toggle-link">
        {isRegister ? 'Bạn đã có tài khoản? ' : 'Chưa có tài khoản? '}
        <span onClick={() => setIsRegister(!isRegister)}>
          {isRegister ? 'Đăng nhập ngay' : 'Đăng ký ngay'}
        </span>
      </p>

      <div className="footer">
        &copy; {new Date().getFullYear()} MySteam. All rights reserved.
      </div>
    </div>
  );
};

export default Login;
