import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

export default function GameDetail({ user, cartItems, setCartItems }) {
  const { gameId } = useParams();
  const navigate = useNavigate();

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const reviewsPerPage = 5;
  const [newRating, setNewRating] = useState(0);
  const [newComment, setNewComment] = useState('');
  const [inCart, setInCart] = useState(false);
  const [suggestedGames, setSuggestedGames] = useState([]);

  const baseURL = axios.defaults.baseURL;
  const buildImageUrl = path => {
    if (!path) return '';
    return path.startsWith('http') ? path : `${baseURL}${path.startsWith('/') ? '' : '/'}${path}`;
  };

  useEffect(() => {
    async function fetchGame() {
      try {
        const res = await axios.get(`/api/games/${gameId}`);
        setGame(res.data);
      } catch (err) {
        console.error('Error fetching game:', err);
      }
    }
    fetchGame();
  }, [gameId]);

  useEffect(() => {
    async function fetchReviews() {
      try {
        const res = await axios.get(`/api/games/${gameId}/reviews`);
        setReviews(res.data.reviews || []);
      } catch (err) {
        console.error('Error fetching reviews:', err);
      }
    }
    fetchReviews();
  }, [gameId]);

  useEffect(() => {
    const exists = cartItems.some(item => +item.gameId === +gameId);
    setInCart(exists);
  }, [cartItems, gameId]);

  useEffect(() => {
    if (game?.genreId) {
      async function fetchSuggestions() {
        try {
          const res = await axios.get(`/api/games?genreId=${game.genreId}&limit=5`);
          const games = res.data.games.filter(g => g.id !== game.id);
          setSuggestedGames(games);
        } catch (err) {
          console.error('Error fetching suggestions:', err);
        }
      }
      fetchSuggestions();
    }
  }, [game]);

  const handleAddToCart = async () => {
    if (!user?.id) {
      alert('Bạn cần đăng nhập để thêm vào giỏ hàng');
      return;
    }
    if (inCart) {
      alert('Đã có trong giỏ hàng');
      return;
    }
    try {
      const res = await axios.post('/api/cart/add', { userId: user.id, gameId: +gameId });
      if (res.status === 201) {
        setInCart(true);
        setCartItems(prev => [...prev, { gameId: +gameId }]);
        navigate('/cart');
      }
    } catch (err) {
      console.error('Add to Cart Error:', err);
      alert(err.response?.data.message || 'Lỗi thêm vào giỏ hàng');
    }
  };

  const handleReviewSubmit = async () => {
    if (!newRating || !newComment.trim()) return;
    try {
      await axios.post(`/api/games/${gameId}/reviews`, {
        user_id: user.id,
        star: newRating,
        comment: newComment.trim(),
      });
      const res = await axios.get(`/api/games/${gameId}/reviews`);
      setReviews(res.data.reviews || []);
      setNewRating(0);
      setNewComment('');
      setCurrentPage(1);
    } catch (err) {
      console.error('Submit review error:', err);
      alert(err.response?.data.error || 'Lỗi gửi đánh giá');
    }
  };

  const indexOfLastReview = currentPage * reviewsPerPage;
  const indexOfFirstReview = indexOfLastReview - reviewsPerPage;
  const currentReviews = reviews.slice(indexOfFirstReview, indexOfLastReview);
  const totalPages = Math.ceil(reviews.length / reviewsPerPage);

  const handlePageChange = page => {
    if (page >= 1 && page <= totalPages) setCurrentPage(page);
  };

  if (!game) return <div className="game-detail"><p>Loading game details...</p></div>;

  return (
    <div className="game-detail">
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart ({cartItems.length})</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

      <section className="detail-banner">
        <img src={buildImageUrl(game.imageUrl)} alt={game.title} />
      </section>

      <div className="detail-main">
        <div className="detail-left">
          <h2>{game.title}</h2>
          <p className="description">{game.description}</p>
          <div className="detail-buy">
            <div className="price">${game.price}</div>
            <button
              className={`buy-button ${inCart ? 'in-cart' : ''}`}
              onClick={handleAddToCart}
            >
              {inCart ? 'In Cart' : 'Add to Cart'}
            </button>
          </div>
          <hr />
          <section className="submit-review">
            <h4>Write a Review</h4>
            <div className="form-group">
              <label htmlFor="rating">Rating:</label>
              <select
                id="rating"
                value={newRating}
                onChange={e => setNewRating(+e.target.value)}
              >
                <option value={0}>Select</option>
                {[1,2,3,4,5].map(n => <option key={n} value={n}>{n}</option>)}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="comment">Comment:</label>
              <textarea
                id="comment"
                placeholder="Write your review..."
                value={newComment}
                onChange={e => setNewComment(e.target.value)}
              />
            </div>
            <button className="submit-btn" onClick={handleReviewSubmit}>
              Submit Review
            </button>
          </section>
        </div>

        <div className="detail-right">
          <img
            className="detail-screenshot"
            src={buildImageUrl(game.screenshotUrl)}
            alt={`${game.title} screenshot`}
          />
          <section className="reviews-section">
            <h4>Đánh giá</h4>
            <div className="reviews-list">
              {currentReviews.length > 0 ? (
                currentReviews.map(r => (
                  <div key={r.id} className="review-item">
                    <strong>User: {r.user_id}</strong>
                    <p>Rating: {r.star} ★</p>
                    <p>{r.comment}</p>
                  </div>
                ))
              ) : (
                <p>Chưa có đánh giá nào.</p>
              )}
            </div>
            {totalPages > 1 && (
              <div className="pagination">
                <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>Trước</button>
                {[...Array(totalPages)].map((_, idx) => (
                  <button
                    key={idx+1}
                    onClick={() => handlePageChange(idx+1)}
                    className={currentPage === idx+1 ? 'active' : ''}
                  >{idx+1}</button>
                ))}
                <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>Tiếp</button>
              </div>
            )}
          </section>
        </div>
      </div>

      <section className="suggestions-section">
        <h4>Các game cùng thể loại</h4>
        <ul className="suggestions-list">
          {suggestedGames.length > 0 ? (
            suggestedGames.map(g => (
              <li key={g.id}>
                <Link to={`/games/${g.id}`}>{g.title}</Link>
              </li>
            ))
          ) : (
            <p>Không có game gợi ý.</p>
          )}
        </ul>
      </section>

      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Logo" className="footer-logo" />
            <p>© 2025 My Game Store</p>
          </div>
          <div className="footer-links">
            <ul><li>About</li><li>Support</li></ul>
            <ul><li>Privacy</li><li>Terms</li></ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon twitter"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
