import React, { useState } from "react";
import { Routes, Route, BrowserRouter as Router } from "react-router-dom";
import Login from "./Login";
import StorePage from "./StorePage";
import GameDetail from "./GameDetail";
import Cart from "./Cart";
import Library from "./Library";
import Profile from "./Profile";
import AddGame from "./AddGame";
import Checkout from './Checkout';

function App() {
  const [cartItems, setCartItems] = useState([]); // Giữ giỏ hàng là mảng

  return (
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/store" element={<StorePage cartItems={cartItems} setCartItems={setCartItems} />} />
        <Route path="/game/:key"    element={<GameDetail />} />
        <Route path="/game/:gameId" element={<GameDetail />} />
        <Route path="/cart" element={<Cart cartItems={cartItems} setCartItems={setCartItems} />} /> 
        <Route path="/library" element={<Library />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/addgame" element={<AddGame />} />
        <Route path="/checkout" element={<Checkout setCartItems={setCartItems} />} />

      </Routes>
  );
}

export default App;
