import React, { useState, useEffect } from 'react';
import { getStoreGames } from './services/apiService';
import './StorePage.css';

export default function StorePage() {
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);

  // pagination state per category
  const [pageInfo, setPageInfo] = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  const PAGE_SIZE = 4;

  // helper to fetch with type and pagination
  const fetchCategory = async (type, setter) => {
    try {
      const { items, total } = await getStoreGames({ type, page: pageInfo[type], pageSize: PAGE_SIZE });
      setter(items);
      setTotalPages(prev => ({ ...prev, [type]: Math.ceil(total / PAGE_SIZE) }));
    } catch (err) {
      console.error(`Failed to fetch ${type}`, err);
    }
  };

  useEffect(() => {
    // featured (first page, ignoring type)
    getStoreGames({ page: 1, pageSize: PAGE_SIZE })
      .then(res => setFeatured(res.items))
      .catch(err => console.error('Failed to fetch featured', err));

    // fetch special, free, best on pageInfo change
    fetchCategory('special', setSpecial);
    fetchCategory('free', setFreeGames);
    fetchCategory('best', setBestSellers);
  }, [pageInfo]);

  const handlePageChange = (type, direction) => {
    setPageInfo(prev => {
      let next = prev[type] + direction;
      if (next < 1) next = 1;
      if (next > totalPages[type]) next = totalPages[type];
      return { ...prev, [type]: next };
    });
  };

  return (
    <div className="store-page">
      <div className="content-container">
        {/* FEATURED */}
        <section className="featured-container">
          <h2>Featured</h2>
          <div className="featured">
            {featured.map(game => (
              <div key={game.id} className="featured-item">
                <img src={game.imageUrl} alt={game.title} />
                <div className="game-info">
                  <h3>{game.title}</h3>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* SPECIAL OFFERS */}
        <section className="special-offers">
          <h2>Special Offers</h2>
          <div className="game-list">
            {special.map(game => (
              <div key={game.id} className="game-card">
                <div className="discount">-{game.discount}%</div>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="prices">
                  <span className="original">${game.price.toFixed(2)}</span>
                  <span className="sale">${(game.price * (1 - game.discount/100)).toFixed(2)}</span>
                </div>
                <div className="sales-count">
                  {(game.salesCount || 0).toLocaleString()} lượt mua
                </div>
              </div>
            ))}
          </div>
          <div className="pagination pagination-right">
            <button onClick={() => handlePageChange('special', -1)} disabled={pageInfo.special === 1}>&lt; Prev</button>
            <span>{pageInfo.special}/{totalPages.special}</span>
            <button onClick={() => handlePageChange('special', 1)} disabled={pageInfo.special === totalPages.special}>Next &gt;</button>
          </div>
        </section>

        {/* FREE-TO-PLAY GAMES */}
        <section className="free-games">
          <h2>Free-to-Play Games</h2>
          <div className="game-list">
            {freeGames.map(game => (
              <div key={game.id} className="game-card">
                <div className="discount">-{game.discount > 0 ? game.discount + '%' : '-%'}</div>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="prices">
                  {game.price > 0 && <span className="original">${game.price.toFixed(2)}</span>}
                  <span className="sale">Free</span>
                </div>
                <div className="sales-count">
                  {(game.salesCount || 0).toLocaleString()} lượt mua
                </div>
              </div>
            ))}
          </div>
          <div className="pagination pagination-right">
            <button onClick={() => handlePageChange('free', -1)} disabled={pageInfo.free === 1}>&lt; Prev</button>
            <span>{pageInfo.free}/{totalPages.free}</span>
            <button onClick={() => handlePageChange('free', 1)} disabled={pageInfo.free === totalPages.free}>Next &gt;</button>
          </div>
        </section>

        {/* BEST SELLERS */}
        <section className="best-seller">
          <h2>Best Sellers</h2>
          <div className="game-list">
            {bestSellers.map(game => (
              <div key={game.id} className="game-card">
                <div className="discount">-{game.discount > 0 ? game.discount + '%' : '-%'}</div>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="prices">
                  {game.price > 0 && <span className="original">${game.price.toFixed(2)}</span>}
                  <span className="sale">${(game.price * (1 - game.discount/100)).toFixed(2)}</span>
                </div>
                <div className="sales-count">
                  {(game.salesCount || 0).toLocaleString()} lượt mua
                </div>
              </div>
            ))}
          </div>
          <div className="pagination pagination-right">
            <button onClick={() => handlePageChange('best', -1)} disabled={pageInfo.best === 1}>&lt; Prev</button>
            <span>{pageInfo.best}/{totalPages.best}</span>
            <button onClick={() => handlePageChange('best', 1)} disabled={pageInfo.best === totalPages.best}>Next &gt;</button>
          </div>
        </section>
      </div>
    </div>
  );
}
