// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Thiết lập baseURL để trỏ tới backend
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  const navigate = useNavigate();
  const { gameId } = useParams();
  const userId = 1; // Thay bằng auth nếu có

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');

  // Fetch thông tin game
  useEffect(() => {
    const fetchGame = async () => {
      try {
        const res = await axios.get(`/api/games/${gameId}`);
        setGame(res.data);
      } catch (err) {
        setError(err.response?.status === 404 ? 'Game not found' : 'Error fetching data');
      } finally {
        setLoading(false);
      }
    };
    fetchGame();
  }, [gameId]);

  // Fetch reviews
  useEffect(() => {
    const fetchReviews = async () => {
      if (!game) return;
      try {
        const res = await axios.get(`/api/reviews`, { params: { gameId: game.id } });
        setReviews(res.data);
      } catch (err) {
        console.error('Error fetching reviews', err);
      }
    };
    fetchReviews();
  }, [game]);

  // Gửi review
  const handleSubmitReview = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/reviews', {
        userId,
        gameId: game.id,
        comment: review,
        rating: Number(rating),
      });
      setShowModal(true);
      // Tải lại reviews
      const res = await axios.get(`/api/reviews`, { params: { gameId: game.id } });
      setReviews(res.data);
    } catch (err) {
      console.error('Error submitting review', err);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error)   return <p>{error}</p>;
  if (!game)   return null;

  return (
    <div className="game-detail">
      <button className="back-btn" onClick={() => navigate(-1)}>← Back</button>

      <div className="banner">
        <img src={game.bannerUrl || placeholderBanner} alt={game.title} />
      </div>

      <h1>{game.title}</h1>
      <p>{game.description}</p>

      <ul className="game-meta">
        <li><strong>Developer:</strong> {game.developer}</li>
        <li><strong>Publisher:</strong> {game.publisher}</li>
        <li><strong>Genre:</strong> {game.genre}</li>
        <li><strong>Price:</strong> ${game.price.toFixed(2)}</li>
        <li><strong>Sales:</strong> {game.salesCount.toLocaleString()}</li>
      </ul>

      <section className="reviews">
        <h2>Reviews</h2>
        {reviews.map(r => (
          <div key={r.id} className="review-card">
            <strong>{r.user.name}</strong> rated {r.rating}/5
            <p>{r.comment}</p>
          </div>
        ))}
      </section>

      <section className="add-review">
        <h2>Add Your Review</h2>
        <form onSubmit={handleSubmitReview}>
          <label>
            Rating:
            <select value={rating} onChange={e => setRating(e.target.value)} required>
              <option value="">Select</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </label>
          <label>
            Comment:
            <textarea value={review} onChange={e => setReview(e.target.value)} required />
          </label>
          <button type="submit">Submit Review</button>
        </form>
      </section>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Thank you for your review!</p>
            <button onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
