import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured]   = useState([]);
  const [special, setSpecial]     = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [games, setGames]         = useState([]);
  const [genres, setGenres]       = useState([]);
  const [selectedGenre, setSelectedGenre] = useState(0);
  const [query, setQuery]         = useState('');
  const [pageInfo, setPageInfo]   = useState({ special: 1, free: 1, best: 1, genre: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1, genre: 1 });

  const navigate = useNavigate();
  const PAGE_SIZE = 5;

  // Lấy list genres
  useEffect(() => {
    axios.get('/api/genres')
      .then(res => {
        const arr = Array.isArray(res.data.data) ? res.data.data : [];
        const list = arr.map(g => ({ id: g.GenreID, name: g.Name }));
        setGenres([{ id: 0, name: 'All' }, ...list]);
      })
      .catch(err => console.error('Error fetching genres:', err));
  }, []);

  // Lấy dữ liệu games + tính tổng trang (chỉ gọi 1 lần)
  useEffect(() => {
    async function fetchData() {
      try {
        // Lấy featured game (trang 1)
        const paramsFeat = { page: 1, pageSize: PAGE_SIZE };
        // Lấy full list để client-side paginate
        const paramsAllLarge = { page: 1, pageSize: 1000 };

        const [resFeat, resSpec, resFree, resBest, resAll] = await Promise.all([
          axios.get('/api/games',      { params: paramsFeat }),
          axios.get('/api/games',      { params: { ...paramsAllLarge, type: 'special' } }),
          axios.get('/api/games',      { params: { ...paramsAllLarge, type: 'free'    } }),
          axios.get('/api/games',      { params: { ...paramsAllLarge, type: 'best'    } }),
          axios.get('/api/games',      { params: paramsAllLarge }),
        ]);

        const feats = resFeat.data.items   || [];
        const specs = resSpec.data.items   || [];
        const frees = resFree.data.items   || [];
        const bests = resBest.data.items   || [];
        const all   = resAll.data.items    || [];

        setFeatured(feats);
        setSpecial(specs);
        setFreeGames(frees);
        setBestSellers(bests);
        setGames(all);

        setTotalPages({
          special: Math.max(1, Math.ceil(specs.length / PAGE_SIZE)),
          free:    Math.max(1, Math.ceil(frees.length / PAGE_SIZE)),
          best:    Math.max(1, Math.ceil(bests.length / PAGE_SIZE)),
          genre:   Math.max(1, Math.ceil(all.length   / PAGE_SIZE)),
        });
      } catch (err) {
        console.error('Error fetching games:', err);
      }
    }
    fetchData();
  }, []);

  // Hàm chuyển trang
  const handlePageChange = (category, delta) => {
    setPageInfo(prev => {
      const next = prev[category] + delta;
      return {
        ...prev,
        [category]: Math.max(1, Math.min(next, totalPages[category]))
      };
    });
  };

  // Chuyển thể loại thì reset page về 1
  const handleGenreSelect = (genreId) => {
    setSelectedGenre(genreId);
    setPageInfo(prev => ({ ...prev, genre: 1 }));
  };

  function safePrice(val) {
    if (val == null) return null;
    const num = Number(String(val).replace(/,/g, ''));
    return Number.isFinite(num) ? num : null;
  }

  // Kiểm tra filter
  const appliesFilter = game => {
    const title   = game.title || game.Title || '';
    const genreId = game.genreId || game.GenreID || 0;
    const matchesQuery = title.toLowerCase().includes(query.toLowerCase());
    const matchesGenre = selectedGenre === 0 || genreId === selectedGenre;
    return matchesQuery && matchesGenre;
  };

  // Mảng đã lọc
  const filteredFeatured = featured.filter(appliesFilter);
  const filteredSpecial  = special.filter(appliesFilter);
  const filteredFree     = freeGames.filter(appliesFilter);
  const filteredBest     = bestSellers.filter(appliesFilter);
  const filteredAll      = games.filter(appliesFilter);

  // Mảng phân trang
  const slicePage = (arr, page) => {
    const start = (page - 1) * PAGE_SIZE;
    return arr.slice(start, start + PAGE_SIZE);
  };

  const paginatedSpecial = slicePage(filteredSpecial, pageInfo.special);
  const paginatedFree    = slicePage(filteredFree, pageInfo.free);
  const paginatedBest    = slicePage(filteredBest, pageInfo.best);
  const paginatedAll     = slicePage(filteredAll, pageInfo.genre);

  return (
    <div className="store-page">
      {/* ... header, banner, search giống cũ ... */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Logo" className="footer-logo" />
          </span>
          <div className="user-actions">
            <button className="download-btn" onClick={handleDiscoverClick}>
              Library
            </button>
            <span className="cart-btn" onClick={() => navigate('/cart')}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate('/profile')}>
              Profile
            </span>
          </div>
        </div>
      </header>
      {/* Search & Genre */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search games..."
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
      </div>
      <div className="genres-container">
        {genres.map(g => (
          <button
            key={g.id}
            className={selectedGenre === g.id ? 'genre-button active' : 'genre-button'}
            onClick={() => handleGenreSelect(g.id)}
          >
            {g.name}
          </button>
        ))}
      </div>

      <div className="content-container">
        {/* Nếu đang lọc (search/genre) */}
        {(query || selectedGenre !== 0) ? (
          <section className="search-results">
            <h2>Filtered Results</h2>
            <div className="game-list">
              {paginatedAll.length ? paginatedAll.map(game => (
                <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                  <img src={game.imageUrl} alt={game.title || game.Name} />
                  <h3>{game.title || game.Name}</h3>
                  {safePrice(game.price) > 0
                    ? <span>${safePrice(game.price).toFixed(2)}</span>
                    : <span>Free</span>
                  }
                </Link>
              )) : <p>No games found.</p>}
            </div>
            <div className="pagination">
              <button onClick={() => handlePageChange('genre', -1)} disabled={pageInfo.genre === 1}>&lt; Prev</button>
              <span>{pageInfo.genre}/{totalPages.genre}</span>
              <button onClick={() => handlePageChange('genre', 1)} disabled={pageInfo.genre === totalPages.genre}>Next &gt;</button>
            </div>
          </section>
        ) : (
          <>
            {/* Featured */}
            <section className="special-offers">
              <h2>Featured Game</h2>
              {filteredFeatured[0] && (
                <div className="featured">
                  <Link to={`/game/${filteredFeatured[0].id}`} className="game-card">
                    <img src={filteredFeatured[0].imageUrl} alt={filteredFeatured[0].title} />
                  </Link>
                  <div className="game-info">
                    <h3>{filteredFeatured[0].title}</h3>
                    <p>{filteredFeatured[0].description}</p>
                    <p>{(filteredFeatured[0].salesCount || 0).toLocaleString()} lượt mua</p>
                  </div>
                </div>
              )}

              {/* Special Deals */}
              <div className="section-card special-section">
                <h2>Special Deals</h2>
                <div className="game-list">
                  {paginatedSpecial.map(game => (
                    <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                      {game.discount > 0 && <div className="discount">-{game.discount}%</div>}
                      <img src={game.imageUrl} alt={game.title}/>
                      <h3>{game.title}</h3>
                      {game.originalPrice > 0 && (
                        <span className="original-price">${game.originalPrice.toFixed(2)}</span>
                      )}
                      <span className="discounted-price">
                        ${(game.originalPrice * (1 - game.discount/100)).toFixed(2)}
                      </span>
                    </Link>
                  ))}
                </div>
                <div className="pagination">
                  <button disabled={pageInfo.special <= 1} onClick={() => handlePageChange('special', -1)}>&lt; Prev</button>
                  <span>{pageInfo.special}/{totalPages.special}</span>
                  <button disabled={pageInfo.special >= totalPages.special} onClick={() => handlePageChange('special', +1)}>Next &gt;</button>
                </div>
              </div>

              {/* Free Games */}
              <div className="section free-section">
                <h2>Free Games</h2>
                <div className="game-list">
                  {paginatedFree.map(game => (
                    <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                      <img src={game.imageUrl} alt={game.title}/>
                      <h3>{game.title}</h3>
                    </Link>
                  ))}
                </div>
                <div className="pagination">
                  <button disabled={pageInfo.free <= 1} onClick={() => handlePageChange('free', -1)}>&lt; Prev</button>
                  <span>{pageInfo.free}/{totalPages.free}</span>
                  <button disabled={pageInfo.free >= totalPages.free} onClick={() => handlePageChange('free', +1)}>Next &gt;</button>
                </div>
              </div>

              {/* Best Sellers */}
              <div className="section best-section">
                <h2>Best Sellers</h2>
                <div className="game-list">
                  {paginatedBest.map(game => (
                    <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                      <img src={game.imageUrl} alt={game.title}/>
                      <h3>{game.title}</h3>
                    </Link>
                  ))}
                </div>
                <div className="pagination">
                  <button disabled={pageInfo.best <= 1} onClick={() => handlePageChange('best', -1)}>&lt; Prev</button>
                  <span>{pageInfo.best}/{totalPages.best}</span>
                  <button disabled={pageInfo.best >= totalPages.best} onClick={() => handlePageChange('best', +1)}>Next &gt;</button>
                </div>
              </div>
            </section>
          </>
        )}
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners in the US and other countries.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul><li>About Valve</li><li>Jobs</li><li>Steamworks</li><li>Steam Distribution</li><li>Support</li></ul>
            <ul><li>Privacy Policy</li><li>Legal</li><li>Steam Subscriber Agreement</li><li>Refunds</li><li>Cookies</li></ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
