// src/Checkout.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Checkout.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Checkout() {
  const navigate = useNavigate();
  const userId = 1; // Thay bằng userId thật từ auth

  const [cartItems, setCartItems] = useState([]);
  const [loadingCart, setLoadingCart] = useState(true);
  const [checkoutLoading, setCheckoutLoading] = useState(false);

  // Fetch giỏ hàng
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`/api/cart/${userId}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('Lấy giỏ hàng lỗi:', err);
      } finally {
        setLoadingCart(false);
      }
    };
    fetchCart();
  }, [userId]);

  // Tính tổng giá trị đơn hàng
  const totalPrice = cartItems.reduce((sum, item) => {
    const raw = typeof item.Price === 'number'
      ? item.Price
      : parseFloat(item.Price) || 0;
    const final = item.Discount > 0
      ? raw * (1 - item.Discount / 100)
      : raw;
    return sum + final;
  }, 0);

  // Xử lý Thanh toán
  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      alert('Giỏ hàng đang trống.');
      return;
    }
    setCheckoutLoading(true);
    try {
      const res = await axios.post('/api/checkout', {
        userId,
        cartItems
      });
      alert('Thanh toán thành công! Mã đơn: ' + res.data.purchaseId);
      navigate('/library'); // Chuyển sang thư viện
    } catch (err) {
      console.error('Checkout lỗi:', err);
      alert('Thanh toán thất bại, vui lòng thử lại.');
    } finally {
      setCheckoutLoading(false);
    }
  };

  if (loadingCart) {
    return <div className="checkout-page"><p>Đang tải giỏ hàng...</p></div>;
  }

  return (
    <div className="checkout-page">
      <header className="checkout-header">
        <h1>Checkout</h1>
        <button onClick={() => navigate(-1)} className="back-btn">
          ← Quay lại
        </button>
      </header>

      <main className="checkout-main">
        {cartItems.length === 0 ? (
          <p>Giỏ hàng của bạn đang trống.</p>
        ) : (
          <>
            <table className="checkout-table">
              <thead>
                <tr>
                  <th>Game</th>
                  <th>Giá gốc</th>
                  <th>Giá cuối</th>
                </tr>
              </thead>
              <tbody>
                {cartItems.map(item => {
                  const raw = typeof item.Price === 'number'
                    ? item.Price
                    : parseFloat(item.Price) || 0;
                  const final = item.Discount > 0
                    ? raw * (1 - item.Discount / 100)
                    : raw;
                  return (
                    <tr key={item.cartItemId}>
                      <td>{item.Name}</td>
                      <td>${raw.toFixed(2)}</td>
                      <td>${final.toFixed(2)}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>

            <div className="checkout-summary">
              <span>Tổng thanh toán:</span>
              <strong>${totalPrice.toFixed(2)}</strong>
            </div>

            <button
              className="checkout-btn"
              onClick={handleCheckout}
              disabled={checkoutLoading}
            >
              {checkoutLoading ? 'Đang xử lý...' : 'Thanh toán'}
            </button>
          </>
        )}
      </main>

      <footer className="checkout-footer">
        © 2025 My Game Store
      </footer>
    </div>
  );
}
