// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Configure axios base URL
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function GameDetail() {
  const { gameId } = useParams();
  const navigate = useNavigate();

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [inCart, setInCart] = useState(false);

  const userId = 1;  // Replace with actual logged-in user ID

  // Fetch game detail and reviews
  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const res = await axios.get(`/api/games/${gameId}`);
        setGame(res.data);

        // Fetch reviews for this game
        const rev = await axios.get(`/api/games/${gameId}/reviews`);
        setReviews(rev.data.reviews || rev.data);
      } catch (err) {
        setError(err.response?.data.error || 'Error loading data');
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [gameId]);

  // Check if game is in cart
  useEffect(() => {
    axios.get(`/api/cart/${userId}`)
      .then(res => {
        const exists = res.data.items.some(
          item => item.gameId === parseInt(gameId, 10)
        );
        setInCart(exists);
      })
      .catch(err => console.error('Cart check error:', err));
  }, [gameId, userId]);

  const handleReviewSubmit = async e => {
    e.preventDefault();
    try {
      // Post review for this game
      await axios.post(`/api/games/${gameId}/reviews`, {
        user_id: userId,
        comment,
        rating: Number(rating)
      });
      setShowModal(true);
      // Refresh reviews list
      const rev = await axios.get(`/api/games/${gameId}/reviews`);
      setReviews(rev.data.reviews || rev.data);
      setRating('');
      setComment('');
    } catch (err) {
      setError(err.response?.data.error || 'Failed to submit review');
    }
  };

  const handleAddToCart = async () => {
    try {
      const res = await axios.post('/api/cart/add', { userId, gameId: parseInt(gameId, 10) });
      if (res.status === 201) {
        setInCart(true);
        alert('Added to cart!');
        navigate('/cart');
      } else {
        alert('Already in cart');
      }
    } catch (err) {
      console.error('Add to cart error:', err);
      alert('Error adding to cart.');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="game-detail">
      <header className="detail-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/')}>GameStore</span>
          <nav>
            <a onClick={() => navigate('/store')}>Store</a>
            <a onClick={() => navigate(-1)}>Back</a>
          </nav>
        </div>
      </header>

      {game && (
        <>
          <div className="detail-banner">
            <img src={game.screenshotUrl} alt={game.title} />
          </div>

          <div className="detail-main">
            <div className="detail-left">
              <h2>{game.title}</h2>
              <p>{game.description}</p>
              <button className="buy-button" onClick={handleAddToCart} disabled={inCart}>
                {inCart ? 'In Cart' : 'Add to Cart'}
              </button>

              <hr />

              <div className="reviews-list">
                {reviews.length > 0 ? reviews.map(r => (
                  <div key={r.id} className="review-item">
                    <strong>User: {r.user_id}</strong>
                    <p>{r.comment}</p>
                    {r.rating !== undefined && <small>Rating: {r.rating}/5</small>}
                  </div>
                )) : (
                  <p>No reviews yet.</p>
                )}
              </div>
            </div>

            <div className="detail-right">
              <img className="detail-screenshot" src={game.screenshotUrl} alt="Screenshot" />
              <div className="system-req">
                <h3>System Requirements</h3>
                <ul>
                  <li>OS: Windows 10</li>
                  <li>CPU: Intel i5</li>
                  <li>RAM: 8 GB</li>
                  <li>GPU: GTX 970</li>
                </ul>
              </div>
            </div>
          </div>

          <section className="submit-review">
            <h4>Write a Review</h4>
            <form onSubmit={handleReviewSubmit}> 

              <div className="form-group">
                <label>Comment</label>
                <textarea value={comment} onChange={e => setComment(e.target.value)} required />
              </div>

              <button type="submit" className="submit-btn">Submit Review</button>
            </form>
          </section>

          {showModal && (
            <div className="modal-overlay">
              <div className="modal">
                <p>Thank you for your review!</p>
                <button onClick={() => setShowModal(false)}>Close</button>
              </div>
            </div>
          )}

          <footer className="detail-footer">
            <div className="footer-content">
              <ul>
                <li>Privacy Policy</li>
                <li>Terms of Service</li>
              </ul>
            </div>
          </footer>
        </>
      )}
    </div>
  );
}
