import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Style/Cart.css';

const Cart = ({ cartItems, setCartItems }) => {
  const navigate = useNavigate();

  const handleRemoveItem = (index) => {
    setCartItems((prevItems) => prevItems.filter((_, i) => i !== index));
  };

  const totalPrice = cartItems
    .map((item) => parseFloat(item.price.replace('$', '').replace('Free', '0')) || 0)
    .reduce((total, price) => total + price, 0);

  const handleDiscoverClick = () => {
    navigate('/library');
  };
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`/api/cart/${userId}`);
        setCartItems(res.data.items);
      } catch (err) {
        console.error('Lấy giỏ hàng lỗi:', err);
      }
    };
    fetchCart();
  }, [setCartItems]);

  return (
    <div className="cart-page">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate("/store")}>
          <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
          </span>
          <div className="user-actions">
          <button className="download-btn" onClick={handleDiscoverClick}>
                Library
            </button>
            <span>Wishlist</span>
            <span className="cart-btn" onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Main container */}
      <main className="main-container">
        <div className="cart-container">
          <h2>Your Shopping Cart</h2>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul className="cart-list">
  {cartItems.map((item, index) => (
    <li key={index} className="cart-item">
      <div className="cart-item-name">
        {item.name || 'Unknown Game'}
      </div>
      <div className="cart-item-price">
        ${item.price || '0.00'}
      </div>
      <button
        className="remove-btn"
        onClick={() => handleRemoveItem(index)}
      >
        X
      </button>
    </li>
  ))}
</ul>

          )}

            <div className="cart-footer">
                {/* Tổng tiền */}
                <div className="cart-total">
                    <h3>Total: ${totalPrice.toFixed(2)}</h3>
                </div>
                {/* Nút Checkout */}
                <button 
  className="checkout-btn" 
  onClick={() => navigate('/checkout', { state: { cartItems } })}
>
  Checkout
</button>
            </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img
              src="/logo.png"
              alt="Valve Steam Logo"
              className="footer-logo"
            />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US
              and other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Cart;
