// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Thiết lập Base URL cho Axios
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  const navigate = useNavigate();
  const { slug } = useParams();

  const [game, setGame] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');

  useEffect(() => {
    const fetchGame = async () => {
      try {
        const res = await axios.get(`/api/games/${slug}`);
        setGame(res.data);
      } catch (err) {
        setError(err.response?.status === 404 ? 'Game not found' : 'Error fetching data');
      } finally {
        setLoading(false);
      }
    };
    fetchGame();
  }, [slug]);

  const handleBuy = () => {
    // logic mua
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/reviews', {
        userId: currentUser.id,
        gameId: game.id,
        comment: review,
        rating: Number(rating)      // gửi thêm số sao
      });
      setShowModal(true);
    } catch (err) {
      console.error(err);
      // hiện thông báo lỗi
    }
  };


  if (loading) return <div className="game-detail"><p>Loading...</p></div>;
  if (error)   return <div className="detail-not-found"><p>{error}</p></div>;

  // Fallback mapping if server returns uppercase keys
  const priceValue   = ('price' in game)     ? game.price     : game.Price;
  const publisherVal = ('publisher' in game) ? game.publisher : game.Publisher;
  const developerVal = ('developer' in game) ? game.developer : game.Developer;
  const handleDiscoverClick = () => {
    navigate('/library');
  };

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate("/store")}>
          <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
          </span>
          <div className="user-actions">
          <button className="download-btn" onClick={handleDiscoverClick}>
                Library
            </button>
            <span>Wishlist</span>
            <span className="cart-btn" onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={game.imageUrl || placeholderBanner} alt={game.title} />
      </div>

      {/* Main */}
      <main className="detail-main">
        <div className="detail-left">
          <h2>{game.title}</h2>
          <p className="description">{game.description}</p>
          {/* Extras */}
            <section id="extras" className="detail-extras">
              <h3>More Information</h3>
              <p>Publisher: {publisherVal}</p>
              <p>Developer: {developerVal}</p>
            </section>
          <div className="detail-buy">
            <span className="price">{priceValue > 0 ? `$${priceValue}` : 'Free'}</span>
            <button className="buy-button" onClick={handleBuy}>
              {priceValue > 0 ? 'Buy Now' : 'Play Now'}
            </button>
          </div>

          <hr />
        </div>

        <div className="detail-right">
          <img className="detail-screenshot" src={game.screenshotUrl} alt="Screenshot" />

          <div className="system-req">
            <h3>System Requirements</h3>
            <ul>
              <li>OS: Windows 10</li>
              <li>CPU: Quad-core</li>
              <li>RAM: 8 GB</li>
            </ul>
          </div>
        </div>
      </main>



      {/* Submit Review */}
      <section className="submit-review">
        <h4>Submit Your Review</h4>
        <form onSubmit={handleSubmitReview}>
          <div className="form-group">
            <label htmlFor="rating">Rating</label>
            <select
              id="rating"
              className="rating-select"
              value={rating}
              onChange={e => setRating(e.target.value)}
              required
            >
              <option value="">Select rating</option>
              <option value="5">5 - Excellent</option>
              <option value="4">4 - Good</option>
              <option value="3">3 - Average</option>
              <option value="2">2 - Poor</option>
              <option value="1">1 - Awful</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="review">Review</label>
            <textarea
              id="review"
              className="review-textarea"
              rows="5"
              placeholder="Write your review..."
              value={review}
              onChange={e => setReview(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="submit-btn">
            Submit Review
          </button>
        </form>
      </section>


      {/* Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Thank you for your review!</p>
            <button onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img
              src="/logo.png"
              alt="Valve Steam Logo"
              className="footer-logo"
            />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US
              and other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>

  );
}
