// src/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import './Style/Login.css'; // nếu có style riêng

const Login = () => {
  // isRegister = true thì hiển thị form Đăng ký, ngược lại là Đăng nhập
  const [isRegister, setIsRegister] = useState(false);
  // state chung cho cả hai form
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    accountNumberBank: ''
  });
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  // cập nhật formData khi input thay đổi
  const handleChange = e => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  // xử lý submit form
  const handleSubmit = async e => {
    e.preventDefault();
    setError('');

    try {
      const url = isRegister
        ? 'http://localhost:5000/api/auth/register'
        : 'http://localhost:5000/api/auth/login';

      // chuẩn bị payload cho register hoặc login
      const payload = isRegister
        ? {
            name: formData.name,
            email: formData.email,
            password: formData.password,
            accountNumberBank: formData.accountNumberBank
          }
        : {
            email: formData.email,
            password: formData.password
          };

      const res = await axios.post(url, payload, {
        headers: { 'Content-Type': 'application/json' }
      });

      // lưu kết quả user trả về
      setUser(res.data);
      // TODO: nếu dùng JWT, lưu token vs. chuyển trang
      console.log('User:', res.data);
    } catch (err) {
      // hiển thị lỗi từ server hoặc lỗi mạng
      setError(
        err.response?.data?.error
          || 'Something went wrong. Please try again.'
      );
    }
  };

  return (
    <div className="login-container">
      <h2>{isRegister ? 'Đăng ký' : 'Đăng nhập'}</h2>

      {user && (
        <div className="welcome">
          <p>Xin chào, <strong>{user.name || user.email}</strong>!</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="login-form">
        {isRegister && (
          <>
            <label>
              Họ & Tên
              <input 
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </label>
            <label>
              Số tài khoản
              <input
                name="accountNumberBank"
                value={formData.accountNumberBank}
                onChange={handleChange}
                required
              />
            </label>
          </>
        )}

        <label>
          Email
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Mật khẩu
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>

        {error && <p className="error-message">{error}</p>}

        <button type="submit" className="btn">
          {isRegister ? 'Đăng ký' : 'Đăng nhập'}
        </button>
      </form>

      <p className="toggle-link">
        {isRegister
          ? 'Bạn đã có tài khoản? '
          : 'Chưa có tài khoản? '}
        <span onClick={() => setIsRegister(!isRegister)}>
          {isRegister ? 'Đăng nhập ngay' : 'Đăng ký ngay'}
        </span>
      </p>
    </div>
  );
};

export default Login;
