import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/AdminPanel.css';

/**
 * AdminPanel (refactored 19-May-2025)
 * ----------------------------------
 * - Hiển thị sidebar (trái) với các mục: Người dùng, Đơn hàng
 * - Nhấn vào mỗi mục sẽ hiện nội dung tương ứng bên phải
 */
const AdminPanel = ({ onLogout, user }) => {
  const token = sessionStorage.getItem('token');
  const [activeTab, setActiveTab] = useState('users'); // 'users' | 'orders'

  /* ------------------ STATE & PAGINATION ------------------ */
  const [users, setUsers]       = useState([]);
  const [orders, setOrders]     = useState([]);
  const [search, setSearch]     = useState('');
  const [userPage, setUserPage] = useState(1);
  const [orderPage, setOrderPage]= useState(1);
  const [totalUsers, setTotalUsers]   = useState(0);
  const [totalOrders, setTotalOrders] = useState(0);
  const pageSize = 5;

  /* ------------------ DATA FETCHING ------------------ */
  useEffect(() => {
    if (activeTab === 'users') fetchUsers();
    // eslint-disable-next-line
  }, [search, userPage, activeTab]);

  useEffect(() => {
    if (activeTab === 'orders') fetchOrders();
    // eslint-disable-next-line
  }, [orderPage, activeTab]);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('/api/admin/users', {
        params: { search, page: userPage, pageSize },
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data.users);
      setTotalUsers(res.data.total);
    } catch (err) {
      console.error('Error fetching users', err);
    }
  };

  const fetchOrders = async () => {
    try {
      const res = await axios.get('/api/admin/orders', {
        params: { page: orderPage, pageSize },
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrders(res.data.orders);
      setTotalOrders(res.data.total);
    } catch (err) {
      console.error('Error fetching orders', err);
    }
  };

  /* ------------------ HANDLERS ------------------ */
  const handleUpdateUser = async (user) => {
    const newName = prompt('Nhập tên mới:', user.Name);
    const newRole = prompt('Nhập vai trò (user/admin):', user.role);
    const newAccount = prompt('Nhập số tài khoản ngân hàng:', user.accountNumberBank || '');
    if (newName != null && newRole != null) {
      try {
        await axios.put(
          `/api/admin/users/${user.id}`,
          { name: newName, role: newRole, accountNumberBank: newAccount },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        fetchUsers();
      } catch (err) {
        console.error('Error updating user', err);
      }
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Bạn có chắc muốn xóa người dùng này?')) return;
    try {
      await axios.delete(`/api/admin/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUsers();
    } catch (err) {
      console.error('Error deleting user', err);
    }
  };

  const handleViewOrder = async (orderId) => {
    try {
      const res = await axios.get(`/api/admin/orders/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(JSON.stringify(res.data.orderDetails, null, 2));
    } catch (err) {
      console.error('Error fetching order details', err);
    }
  };

  /* ------------------ PAGINATION COUNT ------------------ */
  const totalUserPages  = Math.ceil(totalUsers / pageSize);
  const totalOrderPages = Math.ceil(totalOrders / pageSize);

  /* ------------------ RENDER ------------------ */
  return (
    <div className="admin-panel-container">
      {/* ---------- SIDEBAR ---------- */}
      <aside className="admin-sidebar">
        <h2 className="admin-sidebar-title">Quản lý</h2>
        <div
          className={`admin-menu-item ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          Người dùng
        </div>
        <div
          className={`admin-menu-item ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => setActiveTab('orders')}
        >
          Đơn hàng
        </div>
        <button className="admin-logout-btn" onClick={onLogout}>
          Đăng xuất
        </button>
      </aside>

      {/* ---------- MAIN CONTENT ---------- */}
      <main className="admin-main">
        {activeTab === 'users' && (
          <section className="admin-section">
            <h2>Quản lý Người dùng</h2>
            <input
              type="text"
              placeholder="Tìm kiếm người dùng"
              value={search}
              onChange={e => { setSearch(e.target.value); setUserPage(1); }}
            />
            <table className="admin-table">
              <thead>
                <tr>
                  <th>ID</th><th>Name</th><th>Email</th><th>Role</th><th>Account</th><th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(u => (
                  <tr key={u.id}>
                    <td>{u.id}</td><td>{u.name}</td><td>{u.email}</td><td>{u.role}</td><td>{u.accountNumberBank || '-'}</td>
                    <td>
                      <button onClick={() => handleUpdateUser(u)}>Sửa</button>
                      <button onClick={() => handleDeleteUser(u.id)}>Xóa</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Pagination */}
            <div className="admin-pagination">
              <button disabled={userPage === 1} onClick={() => setUserPage(userPage - 1)}>«</button>
              <span>{userPage}/{totalUserPages || 1}</span>
              <button disabled={userPage === totalUserPages} onClick={() => setUserPage(userPage + 1)}>»</button>
            </div>
          </section>
        )}

        {activeTab === 'orders' && (
          <section className="admin-section">
            <h2>Quản lý Đơn hàng</h2>
            <table className="admin-table">
              <thead>
                <tr>
                  <th>ID</th><th>User</th><th>Total</th><th>Date</th><th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(o => (
                  <tr key={o.id}>
                    <td>{o.id}</td><td>{o.userName}</td><td>{o.totalAmount}</td><td>{new Date(o.createdAt).toLocaleDateString()}</td>
                    <td>
                      <button onClick={() => handleViewOrder(o.id)}>Xem</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Pagination */}
            <div className="admin-pagination">
              <button disabled={orderPage === 1} onClick={() => setOrderPage(orderPage - 1)}>«</button>
              <span>{orderPage}/{totalOrderPages || 1}</span>
              <button disabled={orderPage === totalOrderPages} onClick={() => setOrderPage(orderPage + 1)}>»</button>
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

export default AdminPanel;
