import React, { useState, useEffect } from 'react';
import { getStoreGames, getGameReviews } from './services/apiService';
import './Style/StorePage.css';

export default function StorePage() {
  const [games, setGames] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    getStoreGames().then(setGames);
  }, []);

  const handleSearch = async (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    if (term) {
      const res = await getStoreGames(term);
      setSuggestions(res);
    } else {
      setSuggestions([]);
    }
  };

  const selectSuggestion = (game) => {
    setSearchTerm('');
    setSuggestions([]);
    window.location.href = `/game/${game.id}`;
  };

  return (
    <div className="store-page">
      <header className="store-header">
        <div className="nav content-container">
          <span>Steam</span>
          <nav>
            <a href="/">Home</a>
            <a href="/extras">Extras</a>
          </nav>
        </div>
      </header>

      <div className="banner">
        <img src="/banner.jpg" alt="Banner" />
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={handleSearch}
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map((g) => (
              <li key={g.id} onClick={() => selectSuggestion(g)}>
                {g.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      <main className="content-container">
        <section className="featured-container">
          <h2>FEATURED</h2>
          <div className="featured">
            {games.slice(0, 1).map((game) => (
              <div key={game.id} className="featured-item">
                <img src={game.imageUrl} alt={game.title} />
                <div className="game-info">
                  <h3>{game.title}</h3>
                  <p>{game.description}</p>
                </div>
              </div>
            ))}
          </div>
        </section>

        <section className="special-offers">
          <h2>Special Offers</h2>
          <div className="game-list">
            {games
              .filter((g) => g.discount > 0)
              .map((game) => (
                <div key={game.id} className="game-card">
                  <img src={game.imageUrl} alt={game.title} />
                  <span className="discount">-{game.discount}%</span>
                  <h3>{game.title}</h3>
                  <span className="price">
                    ${(game.price * (1 - game.discount / 100)).toFixed(2)}
                  </span>
                </div>
              ))}
          </div>
        </section>
      </main>

      <footer className="footer">
        <div className="footer-content">
          <ul className="footer-links">
            <li>About</li>
            <li>Support</li>
          </ul>
          <ul className="footer-links">
            <li>Privacy Policy</li>
            <li>Legal</li>
            <li>Cookies</li>
          </ul>
        </div>
      </footer>
    </div>
  );
}
