import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "./Style/Profile.css";

// Decode JWT payload without external library
function parseJwt(token) {
  if (!token) return {};
  const parts = token.split('.');
  if (parts.length < 2) return {};
  const base64 = parts[1].replace(/-/g, '+').replace(/_/g, '/');
  try {
    const json = decodeURIComponent(
      atob(base64)
        .split('')
        .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );
    return JSON.parse(json);
  } catch (e) {
    console.error('Error parsing token:', e);
    return {};
  }
}

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [uploadedGames, setUploadedGames] = useState([]);
  const [reviews, setReviews] = useState([]);

  // Retrieve token and extract userId
  const token = localStorage.getItem("token");
  const decoded = parseJwt(token);
  const userId = decoded.sub || decoded.userId;

  useEffect(() => {
    if (!token || !userId) {
      // Not authenticated
      navigate("/login");
      return;
    }

    // Set auth header
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    // 1. Fetch user profile
    axios.get(`/api/users/${userId}`)
      .then(res => setUser(res.data))
      .catch(err => console.error("Error fetching user:", err));

    // 2. Fetch uploaded games
    axios.get(`/api/games?uploader=${userId}`)
      .then(res => {
        const payload = res.data;
        let list = [];
        if (Array.isArray(payload)) list = payload;
        else if (Array.isArray(payload.data)) list = payload.data;
        else if (Array.isArray(payload.games)) list = payload.games;
        setUploadedGames(list);
      })
      .catch(err => console.error("Error fetching games:", err));

    // 3. Fetch user reviews
    axios.get(`/api/users/${userId}/reviews`)
      .then(res => {
        const payload = res.data;
        let list = [];
        if (Array.isArray(payload)) list = payload;
        else if (Array.isArray(payload.data)) list = payload.data;
        else if (Array.isArray(payload.reviews)) list = payload.reviews;
        setReviews(list);
      })
      .catch(err => console.error("Error fetching reviews:", err));

  }, [token, userId, navigate]);
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("user");
    navigate("/login");
  };

  if (!user) {
    return <div className="loading">Loading profile…</div>;
  }

  return (
    <div className="profile-page">
      <header>
        <nav className="nav-bar">
          <Link to="/library" className="nav-link">Discover</Link>
          <Link to="/profile" className="nav-link">Profile</Link>
        </nav>
      </header>

      <main className="profile-container">
        {/* Profile Header */}
        <section className="profile-header">
          {user.avatar
            ? <img src={user.avatar} alt="Avatar" className="avatar" />
            : <div className="avatar placeholder" />
          }
          <div className="user-info">
            <h2>{user.username}</h2>
            {user.level && user.yearsOfService && (
              <p className="info-sub">Level {user.level} • {user.yearsOfService} Years</p>
            )}
            <p>Bank account: {user.bankAccountNumber}</p>
            <p>Games Owned: {user.gamesOwned ?? 0}</p>
            <p>DLCs Owned: {user.dlcsOwned ?? 0}</p>
            <p>Wishlisted: {user.wishlisted ?? 0}</p>
            <p>Reviews: {reviews.length}</p>
          </div>
        </section>

        {/* Recent Activity */}
        <section className="recent-activity">
          <h3>Recent Activity</h3>
          {Array.isArray(user.recentActivity) && user.recentActivity.length > 0
            ? <div className="activity-list">
                {user.recentActivity.map((item, i) => (
                  <div key={i} className="activity-item">
                    <img src={item.image} alt={item.game} />
                    <p>{item.game}</p>
                    <p>{item.hours} hrs</p>
                    <p>Last played: {item.lastPlayed}</p>
                  </div>
                ))}
              </div>
            : <p className="placeholder-text">No recent activity.</p>
          }
        </section>

        <button className="logout-btn" onClick={handleLogout}>
    Đăng xuất
  </button>
      </main>
    </div>
  );
};

export default Profile;
