import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Style/Profile.css";

const Profile = () => {
  const navigate = useNavigate();

  const handleDiscoverClick = () => {
    navigate("/library");
  };

  const handleAddGame = () => {
    navigate("/addgame");
  };

  const user = {
    username: "Juxtopposed",
    avatar: "/avatar.png",
    Bankaccountnumber: "123-456-7890",
    level: 60,
    yearsOfService: 6,
    gamesOwned: 350,
    dlcsOwned: 600,
    reviews: 32,
    wishlisted: 120,
    recentActivity: [
      {
        game: "Mass Effect: Legendary Edition",
        hours: 32,
        lastPlayed: "3 Oct 2023",
        image:
          "https://cdn.akamai.steamstatic.com/steam/apps/1328670/header.jpg",
      },
      {
        game: "Doom",
        hours: 40,
        lastPlayed: "21 Aug 2023",
        image:
          "https://cdn.akamai.steamstatic.com/steam/apps/379720/header.jpg",
      },
      {
        game: "Borderlands 2",
        hours: 15,
        lastPlayed: "15 Jul 2023",
        image:
          "https://cdn.akamai.steamstatic.com/steam/apps/49520/header.jpg",
      },
    ],
  };

  // Dữ liệu các game thực tế trên Steam, không trùng với game cần tránh.
  // Giá được hiển thị bằng USD ($)
  const initialGames = [
    {
      id: 1,
      title: "Elden Ring",
      description:
        "An action RPG set in a vast, dark fantasy world. Players embark on a journey to conquer demigods and claim the Elden Throne.",
      imageUrl:
        "https://cdn.akamai.steamstatic.com/steam/apps/1245620/header.jpg",
      price: "$59.99",
      downloads: 12000000,
    },
    {
      id: 2,
      title: "Cyberpunk 2077",
      description:
        "A futuristic open-world RPG set in Night City. Explore, fight, and hack your way to become a living legend.",
      imageUrl:
        "https://cdn.akamai.steamstatic.com/steam/apps/1091500/header.jpg",
      price: "$59.99",
      downloads: 20000000,
    },
    {
      id: 3,
      title: "Stardew Valley",
      description:
        "A charming farming sim that lets you build a thriving farm, explore caves, and befriend townsfolk.",
      imageUrl:
        "https://cdn.akamai.steamstatic.com/steam/apps/413150/header.jpg",
      price: "$14.99",
      downloads: 20000000,
    },
    {
      id: 4,
      title: "Hades",
      description:
        "A rogue-like dungeon crawler where you defy the god of the dead as you hack and slash out of the Underworld.",
      imageUrl:
        "https://cdn.akamai.steamstatic.com/steam/apps/1145360/header.jpg",
      price: "$24.99",
      downloads: 5000000,
    },
    {
      id: 5,
      title: "Monster Hunter Rise",
      description:
        "Hunt ferocious monsters in a huge variety of locales to become the ultimate hunter.",
      imageUrl:
        "https://cdn.akamai.steamstatic.com/steam/apps/1446780/header.jpg",
      price: "$39.99",
      downloads: 3000000,
    },
  ];

  const [games, setGames] = useState(initialGames);
  const [editingGameId, setEditingGameId] = useState(null);
  const [editedGame, setEditedGame] = useState({
    title: "",
    description: "",
    imageUrl: "",
    price: "",
    // Không bao gồm downloads vì thuộc tính này không thể chỉnh sửa
  });

  // Chuyển sang chế độ chỉnh sửa cho game được chọn
  const startEditing = (game) => {
    setEditingGameId(game.id);
    // Khi chuyển sang chỉnh sửa, giữ nguyên số lượt tải từ game gốc
    setEditedGame({ 
      title: game.title,
      description: game.description,
      imageUrl: game.imageUrl,
      price: game.price,
    });
  };

  // Hủy chế độ chỉnh sửa
  const cancelEditing = () => {
    setEditingGameId(null);
    setEditedGame({
      title: "",
      description: "",
      imageUrl: "",
      price: "",
    });
  };

  // Lưu thay đổi của game sau khi chỉnh sửa; thuộc tính downloads giữ nguyên từ game gốc
  const saveEditing = (id) => {
    // Tìm game gốc để lấy giá trị downloads
    const originalGame = games.find((game) => game.id === id);
    const updatedGame = {
      ...editedGame,
      id,
      downloads: originalGame.downloads,
    };
    setGames(games.map((game) => (game.id === id ? updatedGame : game)));
    setEditingGameId(null);
    setEditedGame({
      title: "",
      description: "",
      imageUrl: "",
      price: "",
    });
  };

  // Xóa game khỏi danh sách
  const deleteGame = (id) => {
    if (window.confirm("Bạn có chắc chắn muốn xóa game này không?")) {
      setGames(games.filter((game) => game.id !== id));
    }
