import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Style/Library.css";

const Library = () => {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  // Lấy userId từ session hoặc param URL
  const storedUser = sessionStorage.getItem("user");
  const parsedUser = storedUser ? JSON.parse(storedUser) : null;
  const currentUserId = parsedUser?.id;
  const userId = paramUserId || currentUserId;

  // State games + loading + search query
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState("");

  useEffect(() => {
    const fetchLibrary = async () => {
      setLoading(true);
      try {
        if (!userId) throw new Error("User ID is missing");
        let res;
        try {
          res = await axios.get(`/api/users/${userId}/library`);
        } catch (err) {
          if (err.response?.status === 404) {
            try {
              res = await axios.get(`/api/users/${userId}/games`);
            } catch {
              res = await axios.get(`/api/library/${userId}`);
            }
          } else {
            throw err;
          }
        }
        console.log("Library API response:", res.data);

        // Xác định mảng rawItems từ response
        let rawItems = [];
        const data = res.data;
        if (Array.isArray(data)) rawItems = data;
        else if (data.items) rawItems = data.items;
        else if (data.data) rawItems = data.data;
        else if (data.library) rawItems = data.library;
        else if (data.games) rawItems = data.games;

        // Trích xuất game IDs hoặc dùng luôn object
        const gameIds = rawItems
          .map(item =>
            item.gameId ?? item.game_id ?? item.id ?? (typeof item === "number" ? item : undefined)
          )
          .filter(id => id !== undefined);

        if (gameIds.length) {
          // Fetch chi tiết từng game
          const detailRes = await Promise.all(
            gameIds.map(id => axios.get(`/api/games/${id}`))
          );
          setGames(detailRes.map(r => r.data));
        } else if (rawItems.length && rawItems[0].id && rawItems[0].title) {
          // rawItems đã là full game objects
          setGames(rawItems);
        } else {
          setGames([]);
        }
      } catch (error) {
        console.error("Error fetching user library:", error);
        setGames([]);
      } finally {
        setLoading(false);
      }
    };
    fetchLibrary();
  }, [userId]);

  // Filter theo query
  const filteredGames = games.filter(game =>
    game.title?.toLowerCase().includes(query.toLowerCase())
  );

  if (loading) {
    return <div className="loading">Loading games for user {userId}...</div>;
  }

  return (
    <div className="library-page">

          <div className="search-bar">
            <input
              type="text"
              placeholder="Search games..."
              value={query}
              onChange={e => setQuery(e.target.value)}
            />
          </div>

        {filteredGames.length === 0 ? (
          <div className="no-games">Không tìm thấy game nào.</div>
        ) : (
          <div className="library-section">
          <div className="library-game-list">
            {filteredGames.map(game => (
              <div key={game.id} className="library-game-card">
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <button
                  onClick={() => {
                    /* play logic */
                  }}
                  className="play-button"
                >
                  Play
                </button>
              </div>
            ))}
          </div>
          </div>
        )}

    </div>
  );
};

export default Library;
