import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import apiService from './services/apiService';
import './Style/StorePage.css';

export default function StorePage() {
  const navigate = useNavigate();
  const ITEMS_PER_PAGE = 4;

  // Search term and suggestions
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  // Pagination and data for each category
  const [specialPage, setSpecialPage] = useState(1);
  const [freePage, setFreePage] = useState(1);
  const [bestPage, setBestPage] = useState(1);

  const [specialData, setSpecialData] = useState({ items: [], total: 0 });
  const [freeData, setFreeData] = useState({ items: [], total: 0 });
  const [bestData, setBestData] = useState({ items: [], total: 0 });

  // Fetch Special Offers
  useEffect(() => {
    apiService
      .getGames({ type: 'special', page: specialPage, pageSize: ITEMS_PER_PAGE })
      .then(res => setSpecialData({ items: res.items, total: res.total }))
      .catch(console.error);
  }, [specialPage]);

  // Fetch Free Games
  useEffect(() => {
    apiService
      .getGames({ type: 'free', page: freePage, pageSize: ITEMS_PER_PAGE })
      .then(res => setFreeData({ items: res.items, total: res.total }))
      .catch(console.error);
  }, [freePage]);

  // Fetch Best Sellers
  useEffect(() => {
    apiService
      .getGames({ type: 'best', page: bestPage, pageSize: ITEMS_PER_PAGE })
      .then(res => setBestData({ items: res.items, total: res.total }))
      .catch(console.error);
  }, [bestPage]);

  // Search suggestions
  useEffect(() => {
    if (searchTerm.trim()) {
      apiService
        .getGames({ search: searchTerm, page: 1, pageSize: 5 })
        .then(res => setSuggestions(res.items))
        .catch(console.error);
    } else {
      setSuggestions([]);
    }
  }, [searchTerm]);

  const totalPages = total => Math.ceil(total / ITEMS_PER_PAGE);

  return (
    <div className="store-page">
      <header className="store-header">
        <div className="nav content-container">
          <span onClick={() => navigate('/')}>STEAM</span>
          <nav>
            <a onClick={() => navigate('/')}>Home</a>
            <a onClick={() => navigate('/extras')}>Extras</a>
          </nav>
        </div>
      </header>

      {/* Search Bar */}
      <div className="search-bar content-container">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map(game => (
              <li key={game.id} onClick={() => navigate(`/game/${game.key}`)}>
                {game.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      <main className="content-container">
        {/* Special Offers Section */}
        <section className="special-offers">
          <h2>Special Offers</h2>
          <div className="game-list">
            {specialData.items.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.key}`)}>
                <div className="discount">-{game.discount}%</div>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="price">
                  {game.price > 0 ? `$${game.price.toFixed(2)}` : 'Free'}
                </div>
                <div className="purchase-count">
                  {game.salesCount.toLocaleString()} lượt mua
                </div>
              </div>
            ))}
          </div>
          {totalPages(specialData.total) > 1 && (
            <div className="pagination">
              <button disabled={specialPage === 1} onClick={() => setSpecialPage(specialPage - 1)}>
                &lt; Prev
              </button>
              <span>{specialPage}/{totalPages(specialData.total)}</span>
              <button
                disabled={specialPage === totalPages(specialData.total)}
                onClick={() => setSpecialPage(specialPage + 1)}>
                Next &gt;
              </button>
            </div>
          )}
        </section>

        {/* Free Games Section */}
        <section className="free-games">
          <h2>Free-to-play Games</h2>
          <div className="game-list">
            {freeData.items.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.key}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="price">Free</div>
                <div className="purchase-count">
                  {game.salesCount.toLocaleString()} lượt mua
                </div>
              </div>
            ))}
          </div>
          {totalPages(freeData.total) > 1 && (
            <div className="pagination">
              <button disabled={freePage === 1} onClick={() => setFreePage(freePage - 1)}>
                &lt; Prev
              </button>
              <span>{freePage}/{totalPages(freeData.total)}</span>
              <button
                disabled={freePage === totalPages(freeData.total)}
                onClick={() => setFreePage(freePage + 1)}>
                Next &gt;
              </button>
            </div>
          )}
        </section>

        {/* Best Sellers Section */}
        <section className="best-seller">
          <h2>Best Sellers</h2>
          <div className="game-list">
            {bestData.items.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.key}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="price">
                  {game.price > 0 ? `$${game.price.toFixed(2)}` : 'Free'}
                </div>
                <div className="purchase-count">
                  {game.salesCount.toLocaleString()} lượt mua
                </div>
              </div>
            ))}
          </div>
          {totalPages(bestData.total) > 1 && (
            <div className="pagination">
              <button disabled={bestPage === 1} onClick={() => setBestPage(bestPage - 1)}>
                &lt; Prev
              </button>
              <span>{bestPage}/{totalPages(bestData.total)}</span>
              <button
                disabled={bestPage === totalPages(bestData.total)}
                onClick={() => setBestPage(bestPage + 1)}>
                Next &gt;
              </button>
            </div>
          )}
        </section>
      </main>

      <footer className="footer">
        <div className="footer-content">
          <ul><li onClick={() => navigate('/about')}>About</li><li onClick={() => navigate('/support')}>Support</li></ul>
          <ul><li onClick={() => navigate('/privacy')}>Privacy Policy</li><li onClick={() => navigate('/legal')}>Legal</li><li onClick={() => navigate('/cookies')}>Cookies</li></ul>
        </div>
      </footer>
    </div>
  );
}
