import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Style/Library.css";

const Library = () => {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  // Lấy userId từ session hoặc param URL
  const storedUser = sessionStorage.getItem("user");
  const parsedUser = storedUser ? JSON.parse(storedUser) : null;
  const currentUserId = parsedUser?.id;
  const userId = paramUserId || currentUserId;

  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);

  // ---- Fetch library games ----
  useEffect(() => {
    const fetchLibrary = async () => {
      setLoading(true);
      try {
        if (!userId) throw new Error("User ID is missing");
        const res = await axios.get(`/api/users/${userId}/library`);
        const raw = Array.isArray(res.data) ? res.data : res.data.items ?? [];

        // Nếu API chỉ trả id => fetch chi tiết
        const first = raw[0];
        if (first && !first.title) {
          const ids = raw.map(it => it.gameId ?? it.id ?? it);
          const details = await Promise.all(ids.map(id => axios.get(`/api/games/${id}`)));
          setGames(details.map(r => r.data));
        } else {
          setGames(raw);
        }
      } catch (e) {
        console.error("Library error", e);
        setGames([]);
      } finally {
        setLoading(false);
      }
    };
    fetchLibrary();
  }, [userId]);

  /* ---------- UI states ---------- */
  if (loading) return <div className="loading">Đang tải thư viện...</div>;
  if (!games.length) return <div className="no-games">Bạn chưa mua game nào.</div>;

  /* ---------- Render ---------- */
  return (
    <div className="library-page">
      <header className="library-header">
        <h1>Steam Library</h1>
        <button className="refresh-btn" onClick={() => window.location.reload()}>Refresh</button>
      </header>

      <div className="library-game-list">
        {games.map(game => (
          <div key={game.id} className="card" onClick={() => navigate(`/game/${game.id}`)}>
            <img src={game.imageUrl} alt={game.title} />
            <div className="card-content">
              <h3>{game.title}</h3>
              <button
                className="play-button"
                onClick={e => {
                  e.stopPropagation();
                  // TODO: play logic
                  alert(`Playing ${game.title}`);
                }}
              >
                Play
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Library;
